    47→  </div>
    48→</body>
    49→
    50→<script type="module" lang="ts">
    51→  import {Scatterplot} from './src/deepscatter';
    52→  import { select } from 'd3-selection';
    53→
    54→  window.select = select; // For the click function below.
    55→
    56→  const prefs = {
    57→    source_url: 'https://bmschmidt.github.io/vietnam_war/',
    58→    max_points: 1000,
    59→    alpha: 10.12, // Target saturation for the full page.
    60→    zoom_balance: 0.12, // Rate at which points increase size. https://observablehq.com/@bmschmidt/zoom-strategies-for-huge-scatterplots-with-three-js
    61→    point_size: 2, // Default point size before application of size scaling
    62→    background_color: '#112233',
    63→    click_function:
    64→      "select('#ident').html(JSON.stringify(datum, undefined, 2))",
    65→    zoom: {
    66→      bbox: {
    67→        x: [99.15172120932304, 114.17888963818825],
    68→        y: [7.0849741134400706, 23.626158008070647],
    69→      },
    70→    },
    71→    encoding: {
    72→      jitter_radius: {
    73→        constant: 0.001,
    74→        method: 'normal',
    75→      },
    76→      color: {
    77→        field: 'MILSERVICE',
    78→        domain: ['VNAF', 'USAF', 'USN', 'USMC', 'RLAF', 'RAAF', 'KAF', 'USA'],
    79→        range: [
    80→          'steelblue',
    81→          'green',
    82→          'navy',
    83→          'green',
    84→          'orange',
    85→          'gray',
    86→          'blue',
    87→          'red',
    88→        ],
    89→        //        range: 'okabe',
    90→        //        domain: [-2047, 2047],
    91→      },
    92→      x: {
    93→        field: 'x',
    94→        transform: 'literal',
    95→      },
    96→      y: {
    97→        field: 'y',
    98→        transform: 'literal',
    99→      },
   100→    },
   101→  };
   102→
   103→  const scatterplot = new Scatterplot('#deepscatter');
   104→  const first_draw = scatterplot.plotAPI(prefs);
   105→  window.plot = scatterplot; // For debugging
   106→
   107→  first_draw.then(async (d) => {
   108→    await scatterplot.add_labels_from_url(
   109→      '/tests/TAKEOFFLOCATION.geojson',
   110→      'takeoff',
   111→      'TAKEOFFLOCATION',
   112→      undefined, // no size field
   113→      { draggable_labels: true, useColorScale: true }
   114→    );
   115→  });
   116→
   117→  // Simple animation demonstration.
   118→
   119→  let cycle = 0;
   120→
   121→  const width = 600;
   122→  function redo_scale() {
   123→    const scale = scatterplot._renderer.aes.store.color.current.scale;
   124→    const domain = scale.domain();
   125→    const range = scale.range();
   126→    select('#color-legend')
   127→      .selectAll('div')
   128→      .data(domain.slice(0, 15))
   129→      .join('div')
   130→      .attr('class', 'legend-div')
   131→      .text((d) => d)
   132→      .style('background-color', (d) => scale(d));
   133→  }
   134→  first_draw.then(() => {
   135→    const holder = select('#left-overlay');
   136→    const buttons = holder
   137→      .selectAll('button')
   138→      .data([
   139→        'MILSERVICE',
   140→        'PERIODOFDAY',
   141→        'UNIT',
   142→        'GEOZONE',
   143→        'AIRFORCEGROUP',
   144→        'TGTTYPE',
   145→        'MFUNC_DESC',
   146→        'TAKEOFFLOCATION',
   147→      ]);
   148→    redo_scale();
   149→    buttons
   150→      .enter()
   151→      .append('div')
   152→      .style('display', 'flex')
   153→      .style('flex', 'row')
   154→      .append('button')
   155→      .text((d) => d)
   156→      .on('click', function (d) {
   157→        const button = select(this);
   158→        const field = button.text();
   159→        scatterplot
   160→          .plotAPI({
   161→            duration: 0.5,
   162→            labels: {
   163→              url: `/tests/${field}.geojson`,
   164→              name: undefined,
   165→              label_field: field,
   166→              size_field: undefined,
   167→            },
   168→            encoding: {
   169→              color: {
   170→                field: field,
   171→                range: 'okabe',
   172→                domain: [-2047, 2047],
   173→              },
   174→            },
   175→          })
   176→          .then(redo_scale);
   177→      });
   178→    select('#date').on('input', function (a, b) {
   179→      console.log(this, a, b);
   180→      const date1 = new Date(1970, 0, this.valueAsNumber - 15);
   181→      const date2 = new Date(1970, 0, this.valueAsNumber + 15);
   182→      document.getElementById('datestring').innerHTML =
   183→        date1.toDateString() + ' to ' + date2.toDateString();
