   210    position: absolute;
   211    top: 0;
   212    background-color: rgba(0, 0, 0, 0.5);
   213    z-index: 99;
   214  }
   215
   216  #left-overlay {
   217    left: 0;
   218  }
   219
   220  #color-legend {
   221    right: 0;
   222    display: flex;
   223    flex-direction: column;
   224    align-items: right;
   225    justify-content: right;
   226  }
   227
   228  .legend-div {
   229    margin: 5px;
   230    padding: 5px 15px;
   231  }
   232
   233  button {
   234    margin: 0.1em;
   235  }
   236
   237  button:hover {
