content.content:
     1â†’import React, { useState, useEffect } from 'react';
     2â†’import { useNavigate } from 'react-router-dom';
     3â†’import {
     4â†’  Button,
     5â†’  Typography,
     6â†’  Spin,
     7â†’  Alert,
     8â†’  Modal,
     9â†’  Form,
    10â†’  Input,
    11â†’  Empty,
    12â†’  Space,
    13â†’  Tag,
    14â†’  Avatar
    15â†’} from 'antd';
    16â†’import {
    17â†’  PlusOutlined,
    18â†’  FileOutlined,
    19â†’  FolderOpenOutlined,
    20â†’  ClockCircleOutlined,
    21â†’  CheckCircleOutlined,
    22â†’  EyeOutlined
    23â†’} from '@ant-design/icons';
    24â†’import { api, Project, FileCheckResponse } from '../utils/api';
    25â†’
    26â†’const { Title, Text } = Typography;
    27â†’const { TextArea } = Input;
    28â†’
    29â†’// Extend Project interface with UI-specific statistics
    30â†’interface ProjectWithStats extends Project {
    31â†’  fileCount: number;
    32â†’  processingCount: number;
    33â†’  completedCount: number;
    34â†’  totalVisualizations: number;
    35â†’}
    36â†’
    37â†’const ProjectDashboard: React.FC = () => {
    38â†’  const [projects, setProjects] = useState<ProjectWithStats[]>([]);
    39â†’  const [loading, setLoading] = useState(true);
    40â†’  const [error, setError] = useState<string | null>(null);
    41â†’  const [createModalVisible, setCreateModalVisible] = useState(false);
    42â†’  const [createLoading, setCreateLoading] = useState(false);
    43â†’  const [form] = Form.useForm();
    44â†’  const navigate = useNavigate();
    45â†’
    46â†’  /**
    47â†’   * Fetches the list of projects and their associated statistics (file count, processing status, visualizations).
    48â†’   */
    49â†’  const fetchProjects = async () => {
    50â†’    setLoading(true);
    51â†’    const result = await api.getProjects();
    52â†’
    53â†’    if (result.error) {
    54â†’      setError(result.error);
    55â†’      setLoading(false);
    56â†’      return;
    57â†’    }
    58â†’
    59â†’    // Fetch file statistics for each project concurrently
    60â†’    const projectsWithStats = await Promise.all(
    61â†’      (result.data || []).map(async (project) => {
    62â†’        const filesResult = await api.getProjectFiles(project.project_id);
    63â†’        let fileCount = 0;
    64â†’        let processingCount = 0;
    65â†’        let completedCount = 0;
    66â†’        let totalVisualizations = 0;
    67â†’
    68â†’        if (!filesResult.error && filesResult.data) {
    69â†’          fileCount = filesResult.data.length;
    70â†’
    71â†’          // Check processing status for each file concurrently
    72â†’          const fileStatuses = await Promise.all(
    73â†’            filesResult.data.map(file => api.checkFileProcessingStatus(file.file_id))
    74â†’          );
    75â†’
    76â†’          for (const status of fileStatuses) {
    77â†’            if (status.status === 'visualization_ready') {
    78â†’              completedCount++;
    79â†’              if (status.fileInfo?.visualization_count) {
    80â†’                totalVisualizations += status.fileInfo.visualization_count;
    81â†’              }
    82â†’            } else if (status.status === 'processing') {
    83â†’              processingCount++;
    84â†’            }
    85â†’          }
    86â†’        }
    87â†’
    88â†’        return {
    89â†’          ...project,
    90â†’          fileCount,
    91â†’          processingCount,
    92â†’          completedCount,
    93â†’          totalVisualizations
    94â†’        };
    95â†’      })
    96â†’    );
    97â†’
    98â†’    setProjects(projectsWithStats);
    99â†’    setLoading(false);
   100â†’  };
   101â†’
   102â†’  // Effect hook to fetch projects on component mount
   103â†’  useEffect(() => {
   104â†’    fetchProjects();
   105â†’  }, []);
   106â†’
   107â†’  /**
   108â†’   * Handles the creation of a new project.
   109â†’   * @param values - The form values containing project name and description.
   110â†’   */
   111â†’  const handleCreateProject = async (values: { name: string; description: string }) => {
   112â†’    setCreateLoading(true);
   113â†’    const result = await api.createProject(values);
   114â†’
   115â†’    if (result.error) {
   116â†’      setError(result.error);
   117â†’    } else {
   118â†’      setCreateModalVisible(false);
   119â†’      form.resetFields();
   120â†’      fetchProjects(); // Refresh the list to show the new project
   121â†’    }
   122â†’
   123â†’    setCreateLoading(false);
   124â†’  };
   125â†’
   126â†’  /**
   127â†’   * Navigates to the project details page.
   128â†’   * @param projectId - The ID of the project to open.
   129â†’   */
   130â†’  const handleProjectClick = (projectId: number) => {
   131â†’    navigate(`/project/${projectId}`);
   132â†’  };
   133â†’
   134â†’  /**
   135â†’   * Navigates to the add data page for a specific project.
   136â†’   * @param projectId - The ID of the project to add data to.
   137â†’   */
   138â†’  const handleAddData = (projectId: number) => {
   139â†’    navigate(`/add-data/${projectId}`);
   140â†’  };
   141â†’
   142â†’  /**
   143â†’   * Determines the status tag for a project based on its file processing statistics.
   144â†’   * @param project - The project with its statistics.
   145â†’   * @returns A React Tag component representing the project status.
   146â†’   */
   147â†’  const getStatusTag = (project: ProjectWithStats) => {
   148â†’    if (project.processingCount > 0) {
   149â†’      return <Tag color="processing" icon={<ClockCircleOutlined />}>Processing</Tag>;
   150â†’    }
   151â†’    if (project.completedCount > 0) {
   152â†’      return <Tag color="success" icon={<CheckCircleOutlined />}>Ready</Tag>;
   153â†’    }
   154â†’    return <Tag color="default">Empty</Tag>;
   155â†’  };
   156â†’
   157â†’  // Display loading spinner while data is being fetched
   158â†’  if (loading) {
   159â†’    return (
   160â†’      <div className="flex justify-center items-center min-h-screen bg-gray-100">
   161â†’        <Spin size="large" />
   162â†’      </div>
   163â†’    );
   164â†’  }
   165â†’
   166â†’  return (
   167â†’    <div className="min-h-screen bg-gray-100 p-4 sm:p-6 lg:p-8 font-sans">
   168â†’      <div className="max-w-7xl mx-auto">
   169â†’        <div className="mb-8">
   170â†’          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 gap-4">
   171â†’            <Title level={2} className="m-0 text-gray-800">Projects</Title>
   172â†’            <Button
   173â†’              type="primary"
   174â†’              icon={<PlusOutlined />}
   175â†’              onClick={() => setCreateModalVisible(true)}
   176â†’              className="w-full sm:w-auto rounded-md shadow-sm"
   177â†’              size="large" // Make button larger
   178â†’            >
   179â†’              Create Project
   180â†’            </Button>
   181â†’          </div>
   182â†’
   183â†’          {error && (
   184â†’            <Alert
   185â†’              message="Error"
   186â†’              description={error}
   187â†’              type="error"
   188â†’              closable
   189â†’              onClose={() => setError(null)}
   190â†’              className="mb-4 rounded-lg"
   191â†’            />
   192â†’          )}
   193â†’
   194â†’          <Text type="secondary" className="text-gray-600 text-base">
   195â†’            Manage your data projects and visualizations
   196â†’          </Text>
   197â†’        </div>
   198â†’
   199â†’        {/* Conditional rendering for empty state or project list */}
   200â†’        {projects.length === 0 ? (
   201â†’          <Empty
   202â†’            image={Empty.PRESENTED_IMAGE_SIMPLE}
   203â†’            description="No projects yet"
   204â†’            className="mt-10 p-6 bg-white rounded-lg shadow-md"
   205â†’          >
   206â†’            <Button
   207â†’              type="primary"
   208â†’              icon={<PlusOutlined />}
   209â†’              onClick={() => setCreateModalVisible(true)}
   210â†’              className="mt-4 rounded-md shadow-sm"
   211â†’              size="large" // Make button larger
   212â†’            >
   213â†’              Create Your First Project
   214â†’            </Button>
   215â†’          </Empty>
   216â†’        ) : (
   217â†’          <div className="grid gap-4 md:grid-cols-1 lg:grid-cols-2 xl:grid-cols-3">
   218â†’            {projects.map((project) => (
   219â†’              <div
   220â†’                key={project.project_id}
   221â†’                className="hover:bg-blue-50 transition-colors duration-200 rounded-lg p-5 border border-gray-200 bg-white shadow-md flex flex-col"
   222â†’              >
   223â†’                <div className="flex items-start justify-between mb-4">
   224â†’                  <div className="flex items-start space-x-4 flex-1">
   225â†’                    <Avatar
   226â†’                      size={64}
   227â†’                      icon={<FolderOpenOutlined />}
   228â†’                      className="bg-blue-600 text-white flex-shrink-0 shadow-sm"
   229â†’                      style={{ borderRadius: '12px' }} // Slightly more rounded than default Ant Design
   230â†’                    />
   231â†’
   232â†’                    <div className="flex-1 min-w-0">
   233â†’                      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-2">
   234â†’                        <Title level={4} className="m-0 truncate text-gray-900 leading-tight">
   235â†’                          {project.name}
   236â†’                        </Title>
   237â†’                        <div className="mt-2 sm:mt-0 sm:ml-4 flex-shrink-0">
   238â†’                          {getStatusTag(project)}
   239â†’                        </div>
   240â†’                      </div>
   241â†’
   242â†’                      <div className="space-y-3 mt-2">
   243â†’                        <Text type="secondary" className="text-sm block text-gray-700">
   244â†’                          {project.description || 'No description provided for this project.'}
   245â†’                        </Text>
   246â†’
   247â†’                        <div className="flex flex-wrap items-center gap-x-6 gap-y-3 pt-2 border-t border-gray-100 mt-3">
   248â†’                          <Space className="items-center text-gray-700">
   249â†’                            <FileOutlined className="text-gray-500" />
   250â†’                            <Text strong className="text-gray-900">{project.fileCount}</Text>
   251â†’                            <Text type="secondary">Files</Text>
   252â†’                          </Space>
   253â†’
   254â†’                          <Space className="items-center text-green-700">
   255â†’                            <CheckCircleOutlined className="text-green-500" />
   256â†’                            <Text strong className="text-green-600">{project.completedCount}</Text>
   257â†’                            <Text type="secondary">Completed</Text>
   258â†’                          </Space>
   259â†’
   260â†’                          {project.totalVisualizations > 0 && (
   261â†’                            <Space className="items-center text-blue-700">
   262â†’                              <EyeOutlined className="text-blue-500" />
   263â†’                              <Text strong className="text-blue-600">{project.totalVisualizations}</Text>
   264â†’                              <Text type="secondary">Visualizations</Text>
   265â†’                            </Space>
   266â†’                          )}
   267â†’
   268â†’                          {project.processingCount > 0 && (
   269â†’                            <Tag color="processing" icon={<ClockCircleOutlined />} className="text-sm">
   270â†’                              {project.processingCount} processing
   271â†’                            </Tag>
   272â†’                          )}
   273â†’                        </div>
   274â†’
   275â†’                        <Text type="secondary" className="text-xs block text-gray-500 mt-2">
   276â†’                          Created: {new Date(project.created_at).toLocaleDateString()}
   277â†’                        </Text>
   278â†’                      </div>
   279â†’                    </div>
   280â†’                  </div>
   281â†’                </div>
   282â†’                {/* Action buttons at the bottom of the card */}
   283â†’                <div className="flex flex-col sm:flex-row items-stretch sm:items-center space-y-2 sm:space-y-0 sm:space-x-2 mt-4 pt-4 border-t border-gray-100">
   284â†’                  <Button
   285â†’                    type="primary"
   286â†’                    icon={<FolderOpenOutlined />}
   287â†’                    onClick={() => handleProjectClick(project.project_id)}
   288â†’                    className="flex-1 rounded-md shadow-sm"
   289â†’                    size="large"
   290â†’                  >
   291â†’                    Open
   292â†’                  </Button>
   293â†’                  <Button
   294â†’                    icon={<PlusOutlined />}
   295â†’                    onClick={() => handleAddData(project.project_id)}
   296â†’                    className="flex-1 rounded-md shadow-sm"
   297â†’                    size="large"
   298â†’                  >
   299â†’                    Add Data
   300â†’                  </Button>
   301â†’                </div>
   302â†’              </div>
   303â†’            ))}
   304â†’          </div>
   305â†’        )}
   306â†’
   307â†’        {/* Create Project Modal */}
   308â†’        <Modal
   309â†’          title={<Title level={3} className="m-0">Create New Project</Title>}
   310â†’          open={createModalVisible}
   311â†’          onCancel={() => {
   312â†’            setCreateModalVisible(false);
   313â†’            form.resetFields();
   314â†’          }}
   315â†’          footer={null}
   316â†’          destroyOnClose
   317â†’          centered
   318â†’          className="rounded-lg shadow-xl"
   319â†’        >
   320â†’          <Form
   321â†’            form={form}
   322â†’            layout="vertical"
   323â†’            onFinish={handleCreateProject}
   324â†’            className="mt-4"
   325â†’          >
   326â†’            <Form.Item
   327â†’              name="name"
   328â†’              label={<Text strong>Project Name</Text>}
   329â†’              rules={[
   330â†’                { required: true, message: 'Please enter a project name' },
   331â†’                { min: 2, message: 'Name must be at least 2 characters' }
   332â†’              ]}
   333â†’            >
   334â†’              <Input placeholder="e.g., Q2 Financial Report" className="rounded-md" />
   335â†’            </Form.Item>
   336â†’
   337â†’            <Form.Item
   338â†’              name="description"
   339â†’              label={<Text strong>Description</Text>}
   340â†’              rules={[
   341â†’                { required: true, message: 'Please enter a description' }
   342â†’              ]}
   343â†’            >
   344â†’              <TextArea
   345â†’                rows={4}
   346â†’                placeholder="Briefly describe the purpose of this project and the data it will contain."
   347â†’                className="rounded-md"
   348â†’              />
   349â†’            </Form.Item>
   350â†’
   351â†’            <div className="flex justify-end space-x-3 mt-6">
   352â†’              <Button
   353â†’                onClick={() => {
   354â†’                  setCreateModalVisible(false);
   355â†’                  form.resetFields();
   356â†’                }}
   357â†’                className="rounded-md"
   358â†’                size="large"
   359â†’              >
   360â†’                Cancel
   361â†’              </Button>
   362â†’              <Button
   363â†’                type="primary"
   364â†’                htmlType="submit"
   365â†’                loading={createLoading}
   366â†’                className="rounded-md shadow-md"
   367â†’                size="large"
   368â†’              >
   369â†’                Create Project
   370â†’              </Button>
   371â†’            </div>
   372â†’          </Form>
   373â†’        </Modal>
   374â†’      </div>
   375â†’    </div>
   376â†’  );
   377â†’};
   378â†’
   379â†’export default ProjectDashboard;

