     1â†’import React, { useState, useEffect } from 'react';
     2â†’import { useNavigate } from 'react-router-dom';
     3â†’import {
     4â†’  Button,
     5â†’  Typography,
     6â†’  Spin,
     7â†’  Alert,
     8â†’  Modal,
     9â†’  Form,
    10â†’  Input,
    11â†’  Empty,
    12â†’  Space,
    13â†’  Tag,
    14â†’  Avatar
    15â†’} from 'antd';
    16â†’import {
    17â†’  PlusOutlined,
    18â†’  FileOutlined,
    19â†’  FolderOpenOutlined,
    20â†’  ClockCircleOutlined,
    21â†’  CheckCircleOutlined,
    22â†’  EyeOutlined
    23â†’} from '@ant-design/icons';
    24â†’import { api, Project, FileCheckResponse } from '../utils/api';
    25â†’
    26â†’const { Title, Text } = Typography;
    27â†’const { TextArea } = Input;
    28â†’
    29â†’// Extend Project interface with UI-specific statistics
    30â†’interface ProjectWithStats extends Project {
    31â†’  fileCount: number;
    32â†’  processingCount: number;
    33â†’  completedCount: number;
    34â†’  totalVisualizations: number;
    35â†’}
    36â†’
    37â†’const ProjectDashboard: React.FC = () => {
    38â†’  const [projects, setProjects] = useState<ProjectWithStats[]>([]);
    39â†’  const [loading, setLoading] = useState(true);
    40â†’  const [error, setError] = useState<string | null>(null);
    41â†’  const [createModalVisible, setCreateModalVisible] = useState(false);
    42â†’  const [createLoading, setCreateLoading] = useState(false);
    43â†’  const [form] = Form.useForm();
    44â†’  const navigate = useNavigate();
    45â†’
    46â†’  /**
    47â†’   * Fetches the list of projects and their associated statistics (file count, processing status, visualizations).
    48â†’   */
    49â†’  const fetchProjects = async () => {
    50â†’    setLoading(true);
    51â†’    const result = await api.getProjects();
    52â†’
    53â†’    if (result.error) {
    54â†’      setError(result.error);
    55â†’      setLoading(false);
    56â†’      return;
    57â†’    }
    58â†’
    59â†’    // Fetch file statistics for each project concurrently
    60â†’    const projectsWithStats = await Promise.all(
    61â†’      (result.data || []).map(async (project) => {
    62â†’        const filesResult = await api.getProjectFiles(project.project_id);
    63â†’        let fileCount = 0;
    64â†’        let processingCount = 0;
    65â†’        let completedCount = 0;
    66â†’        let totalVisualizations = 0;
    67â†’
    68â†’        if (!filesResult.error && filesResult.data) {
    69â†’          fileCount = filesResult.data.length;
    70â†’
    71â†’          // Check processing status for each file concurrently
    72â†’          const fileStatuses = await Promise.all(
    73â†’            filesResult.data.map(file => api.checkFileProcessingStatus(file.file_id))
    74â†’          );
    75â†’
    76â†’          for (const status of fileStatuses) {
    77â†’            if (status.status === 'visualization_ready') {
    78â†’              completedCount++;
    79â†’              if (status.fileInfo?.visualization_count) {
    80â†’                totalVisualizations += status.fileInfo.visualization_count;
    81â†’              }
    82â†’            } else if (status.status === 'processing') {
    83â†’              processingCount++;
    84â†’            }
    85â†’          }
    86â†’        }
    87â†’
    88â†’        return {
    89â†’          ...project,
    90â†’          fileCount,
    91â†’          processingCount,
    92â†’          completedCount,
    93â†’          totalVisualizations
    94â†’        };
    95â†’      })
    96â†’    );
    97â†’
    98â†’    setProjects(projectsWithStats);
    99â†’    setLoading(false);
   100â†’  };
   101â†’
   102â†’  // Effect hook to fetch projects on component mount
   103â†’  useEffect(() => {
   104â†’    fetchProjects();
   105â†’  }, []);
   106â†’
   107â†’  /**
   108â†’   * Handles the creation of a new project.
   109â†’   * @param values - The form values containing project name and description.
   110â†’   */
   111â†’  const handleCreateProject = async (values: { name: string; description: string }) => {
   112â†’    setCreateLoading(true);
   113â†’    const result = await api.createProject(values);
   114â†’
   115â†’    if (result.error) {
   116â†’      setError(result.error);
   117â†’    } else {
   118â†’      setCreateModalVisible(false);
   119â†’      form.resetFields();
   120â†’      fetchProjects(); // Refresh the list to show the new project
   121â†’    }
   122â†’
   123â†’    setCreateLoading(false);
   124â†’  };
   125â†’
   126â†’  /**
   127â†’   * Navigates to the project details page.
   128â†’   * @param projectId - The ID of the project to open.
   129â†’   */
   130â†’  const handleProjectClick = (projectId: number) => {
   131â†’    navigate(`/project/${projectId}`);
   132â†’  };
   133â†’
   134â†’  /**
   135â†’   * Navigates to the add data page for a specific project.
   136â†’   * @param projectId - The ID of the project to add data to.
   137â†’   */
   138â†’  const handleAddData = (projectId: number) => {
   139â†’    navigate(`/add-data/${projectId}`);
   140â†’  };
   141â†’
   142â†’  /**
