content.content:
     1→import React, { useState, useEffect } from 'react';
     2→import { useNavigate } from 'react-router-dom';
     3→import {
     4→  Button,
     5→  Typography,
     6→  Spin,
     7→  Alert,
     8→  Modal,
     9→  Form,
    10→  Input,
    11→  Empty,
    12→  Space,
    13→  Tag,
    14→  Avatar
    15→} from 'antd';
    16→import {
    17→  PlusOutlined,
    18→  FileOutlined,
    19→  FolderOpenOutlined,
    20→  ClockCircleOutlined,
    21→  CheckCircleOutlined,
    22→  EyeOutlined
    23→} from '@ant-design/icons';
    24→import { api, Project, FileCheckResponse } from '../utils/api';
    25→
    26→const { Title, Text } = Typography;
    27→const { TextArea } = Input;
    28→
    29→// Extend Project interface with UI-specific statistics
    30→interface ProjectWithStats extends Project {
    31→  fileCount: number;
    32→  processingCount: number;
    33→  completedCount: number;
    34→  totalVisualizations: number;
    35→}
    36→
    37→const ProjectDashboard: React.FC = () => {
    38→  const [projects, setProjects] = useState<ProjectWithStats[]>([]);
    39→  const [loading, setLoading] = useState(true);
    40→  const [error, setError] = useState<string | null>(null);
    41→  const [createModalVisible, setCreateModalVisible] = useState(false);
    42→  const [createLoading, setCreateLoading] = useState(false);
    43→  const [form] = Form.useForm();
    44→  const navigate = useNavigate();
    45→
    46→  /**
    47→   * Fetches the list of projects and their associated statistics (file count, processing status, visualizations).
    48→   */
    49→  const fetchProjects = async () => {
    50→    setLoading(true);
    51→    const result = await api.getProjects();
    52→
    53→    if (result.error) {
    54→      setError(result.error);
    55→      setLoading(false);
    56→      return;
    57→    }
    58→
    59→    // Fetch file statistics for each project concurrently
    60→    const projectsWithStats = await Promise.all(
    61→      (result.data || []).map(async (project) => {
    62→        const filesResult = await api.getProjectFiles(project.project_id);
    63→        let fileCount = 0;
    64→        let processingCount = 0;
    65→        let completedCount = 0;
    66→        let totalVisualizations = 0;
    67→
    68→        if (!filesResult.error && filesResult.data) {
    69→          fileCount = filesResult.data.length;
    70→
    71→          // Check processing status for each file concurrently
    72→          const fileStatuses = await Promise.all(
    73→            filesResult.data.map(file => api.checkFileProcessingStatus(file.file_id))
    74→          );
    75→
    76→          for (const status of fileStatuses) {
    77→            if (status.status === 'visualization_ready') {
    78→              completedCount++;
    79→              if (status.fileInfo?.visualization_count) {
    80→                totalVisualizations += status.fileInfo.visualization_count;
    81→              }
    82→            } else if (status.status === 'processing') {
    83→              processingCount++;
    84→            }
    85→          }
    86→        }
    87→
    88→        return {
    89→          ...project,
    90→          fileCount,
    91→          processingCount,
    92→          completedCount,
    93→          totalVisualizations
    94→        };
    95→      })
    96→    );
    97→
    98→    setProjects(projectsWithStats);
    99→    setLoading(false);
   100→  };
   101→
   102→  // Effect hook to fetch projects on component mount
   103→  useEffect(() => {
   104→    fetchProjects();
   105→  }, []);
   106→
   107→  /**
   108→   * Handles the creation of a new project.
   109→   * @param values - The form values containing project name and description.
   110→   */
   111→  const handleCreateProject = async (values: { name: string; description: string }) => {
   112→    setCreateLoading(true);
   113→    const result = await api.createProject(values);
   114→
   115→    if (result.error) {
   116→      setError(result.error);
   117→    } else {
   118→      setCreateModalVisible(false);
   119→      form.resetFields();
   120→      fetchProjects(); // Refresh the list to show the new project
   121→    }
   122→
   123→    setCreateLoading(false);
   124→  };
   125→
   126→  /**
   127→   * Navigates to the project details page.
   128→   * @param projectId - The ID of the project to open.
   129→   */
   130→  const handleProjectClick = (projectId: number) => {
   131→    navigate(`/project/${projectId}`);
   132→  };
   133→
   134→  /**
   135→   * Navigates to the add data page for a specific project.
   136→   * @param projectId - The ID of the project to add data to.
   137→   */
   138→  const handleAddData = (projectId: number) => {
   139→    navigate(`/add-data/${projectId}`);
   140→  };
   141→
   142→  /**
   143→   * Determines the status tag for a project based on its file processing statistics.
   144→   * @param project - The project with its statistics.
   145→   * @returns A React Tag component representing the project status.
   146→   */
   147→  const getStatusTag = (project: ProjectWithStats) => {
   148→    if (project.processingCount > 0) {
   149→      return <Tag color="processing" icon={<ClockCircleOutlined />}>Processing</Tag>;
   150→    }
   151→    if (project.completedCount > 0) {
   152→      return <Tag color="success" icon={<CheckCircleOutlined />}>Ready</Tag>;
   153→    }
   154→    return <Tag color="default">Empty</Tag>;
   155→  };
   156→
   157→  // Display loading spinner while data is being fetched
   158→  if (loading) {
   159→    return (
   160→      <div className="flex justify-center items-center min-h-screen bg-gray-100">
   161→        <Spin size="large" />
   162→      </div>
   163→    );
   164→  }
   165→
   166→  return (
   167→    <div className="min-h-screen bg-gray-100 p-4 sm:p-6 lg:p-8 font-sans">
   168→      <div className="max-w-7xl mx-auto">
   169→        <div className="mb-8">
   170→          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 gap-4">
   171→            <Title level={2} className="m-0 text-gray-800">Projects</Title>
   172→            <Button
   173→              type="primary"
   174→              icon={<PlusOutlined />}
   175→              onClick={() => setCreateModalVisible(true)}
   176→              className="w-full sm:w-auto rounded-md shadow-sm"
   177→              size="large" // Make button larger
   178→            >
   179→              Create Project
   180→            </Button>
   181→          </div>
   182→
   183→          {error && (
   184→            <Alert
   185→              message="Error"
   186→              description={error}
   187→              type="error"
   188→              closable
   189→              onClose={() => setError(null)}
   190→              className="mb-4 rounded-lg"
   191→            />
   192→          )}
   193→
   194→          <Text type="secondary" className="text-gray-600 text-base">
   195→            Manage your data projects and visualizations
   196→          </Text>
   197→        </div>
   198→
   199→        {/* Conditional rendering for empty state or project list */}
   200→        {projects.length === 0 ? (
   201→          <Empty
   202→            image={Empty.PRESENTED_IMAGE_SIMPLE}
   203→            description="No projects yet"
   204→            className="mt-10 p-6 bg-white rounded-lg shadow-md"
   205→          >
   206→            <Button
   207→              type="primary"
   208→              icon={<PlusOutlined />}
   209→              onClick={() => setCreateModalVisible(true)}
   210→              className="mt-4 rounded-md shadow-sm"
   211→              size="large" // Make button larger
   212→            >
   213→              Create Your First Project
   214→            </Button>
   215→          </Empty>
   216→        ) : (
   217→          <div className="grid gap-4 md:grid-cols-1 lg:grid-cols-2 xl:grid-cols-3">
   218→            {projects.map((project) => (
   219→              <div
   220→                key={project.project_id}
   221→                className="hover:bg-blue-50 transition-colors duration-200 rounded-lg p-5 border border-gray-200 bg-white shadow-md flex flex-col"
   222→              >
   223→                <div className="flex items-start justify-between mb-4">
   224→                  <div className="flex items-start space-x-4 flex-1">
   225→                    <Avatar
   226→                      size={64}
   227→                      icon={<FolderOpenOutlined />}
   228→                      className="bg-blue-600 text-white flex-shrink-0 shadow-sm"
   229→                      style={{ borderRadius: '12px' }} // Slightly more rounded than default Ant Design
   230→                    />
   231→
   232→                    <div className="flex-1 min-w-0">
   233→                      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-2">
   234→                        <Title level={4} className="m-0 truncate text-gray-900 leading-tight">
   235→                          {project.name}
   236→                        </Title>
   237→                        <div className="mt-2 sm:mt-0 sm:ml-4 flex-shrink-0">
   238→                          {getStatusTag(project)}
   239→                        </div>
   240→                      </div>
   241→
   242→                      <div className="space-y-3 mt-2">
   243→                        <Text type="secondary" className="text-sm block text-gray-700">
   244→                          {project.description || 'No description provided for this project.'}
   245→                        </Text>
   246→
   247→                        <div className="flex flex-wrap items-center gap-x-6 gap-y-3 pt-2 border-t border-gray-100 mt-3">
   248→                          <Space className="items-center text-gray-700">
   249→                            <FileOutlined className="text-gray-500" />
   250→                            <Text strong className="text-gray-900">{project.fileCount}</Text>
   251→                            <Text type="secondary">Files</Text>
   252→                          </Space>
   253→
   254→                          <Space className="items-center text-green-700">
   255→                            <CheckCircleOutlined className="text-green-500" />
   256→                            <Text strong className="text-green-600">{project.completedCount}</Text>
   257→                            <Text type="secondary">Completed</Text>
   258→                          </Space>
   259→
   260→                          {project.totalVisualizations > 0 && (
   261→                            <Space className="items-center text-blue-700">
   262→                              <EyeOutlined className="text-blue-500" />
   263→                              <Text strong className="text-blue-600">{project.totalVisualizations}</Text>
   264→                              <Text type="secondary">Visualizations</Text>
   265→                            </Space>
   266→                          )}
   267→
   268→                          {project.processingCount > 0 && (
   269→                            <Tag color="processing" icon={<ClockCircleOutlined />} className="text-sm">
   270→                              {project.processingCount} processing
   271→                            </Tag>
   272→                          )}
   273→                        </div>
   274→
   275→                        <Text type="secondary" className="text-xs block text-gray-500 mt-2">
   276→                          Created: {new Date(project.created_at).toLocaleDateString()}
   277→                        </Text>
   278→                      </div>
   279→                    </div>
   280→                  </div>
   281→                </div>
   282→                {/* Action buttons at the bottom of the card */}
   283→                <div className="flex flex-col sm:flex-row items-stretch sm:items-center space-y-2 sm:space-y-0 sm:space-x-2 mt-4 pt-4 border-t border-gray-100">
   284→                  <Button
   285→                    type="primary"
   286→                    icon={<FolderOpenOutlined />}
   287→                    onClick={() => handleProjectClick(project.project_id)}
   288→                    className="flex-1 rounded-md shadow-sm"
   289→                    size="large"
   290→                  >
   291→                    Open
   292→                  </Button>
   293→                  <Button
   294→                    icon={<PlusOutlined />}
   295→                    onClick={() => handleAddData(project.project_id)}
   296→                    className="flex-1 rounded-md shadow-sm"
   297→                    size="large"
   298→                  >
   299→                    Add Data
   300→                  </Button>
   301→                </div>
   302→              </div>
   303→            ))}
   304→          </div>
   305→        )}
   306→
   307→        {/* Create Project Modal */}
   308→        <Modal
   309→          title={<Title level={3} className="m-0">Create New Project</Title>}
   310→          open={createModalVisible}
   311→          onCancel={() => {
   312→            setCreateModalVisible(false);
   313→            form.resetFields();
   314→          }}
   315→          footer={null}
   316→          destroyOnClose
   317→          centered
   318→          className="rounded-lg shadow-xl"
   319→        >
   320→          <Form
   321→            form={form}
   322→            layout="vertical"
   323→            onFinish={handleCreateProject}
   324→            className="mt-4"
   325→          >
   326→            <Form.Item
   327→              name="name"
   328→              label={<Text strong>Project Name</Text>}
   329→              rules={[
   330→                { required: true, message: 'Please enter a project name' },
   331→                { min: 2, message: 'Name must be at least 2 characters' }
   332→              ]}
   333→            >
   334→              <Input placeholder="e.g., Q2 Financial Report" className="rounded-md" />
   335→            </Form.Item>
   336→
   337→            <Form.Item
   338→              name="description"
   339→              label={<Text strong>Description</Text>}
   340→              rules={[
   341→                { required: true, message: 'Please enter a description' }
   342→              ]}
   343→            >
   344→              <TextArea
   345→                rows={4}
   346→                placeholder="Briefly describe the purpose of this project and the data it will contain."
   347→                className="rounded-md"
   348→              />
   349→            </Form.Item>
   350→
   351→            <div className="flex justify-end space-x-3 mt-6">
   352→              <Button
   353→                onClick={() => {
   354→                  setCreateModalVisible(false);
   355→                  form.resetFields();
   356→                }}
   357→                className="rounded-md"
   358→                size="large"
   359→              >
   360→                Cancel
   361→              </Button>
   362→              <Button
   363→                type="primary"
   364→                htmlType="submit"
   365→                loading={createLoading}
   366→                className="rounded-md shadow-md"
   367→                size="large"
   368→              >
   369→                Create Project
   370→              </Button>
   371→            </div>
   372→          </Form>
   373→        </Modal>
   374→      </div>
   375→    </div>
   376→  );
   377→};
   378→
   379→export default ProjectDashboard;

