     1â†’import React, { useState, useEffect } from 'react';
     2â†’import { useParams, useNavigate } from 'react-router-dom';
     3â†’import {
     4â†’  Card,
     5â†’  Button,
     6â†’  Row,
     7â†’  Col,
     8â†’  Typography,
     9â†’  Spin,
    10â†’  Alert,
    11â†’  Tag,
    12â†’  Space,
    13â†’  Tooltip,
    14â†’  Empty,
    15â†’  Modal,
    16â†’  Select,
    17â†’  message,
    18â†’  Descriptions,
    19â†’  Badge,
    20â†’  Collapse,
    21â†’  Divider
    22â†’} from 'antd';
    23â†’import {
    24â†’  FileOutlined,
    25â†’  EyeOutlined,
    26â†’  PlayCircleOutlined,
    27â†’  CheckCircleOutlined,
    28â†’  ClockCircleOutlined,
    29â†’  ExclamationCircleOutlined,
    30â†’  ReloadOutlined,
    31â†’  UploadOutlined,
    32â†’  DatabaseOutlined,
    33â†’  DownOutlined,
    34â†’  UpOutlined,
    35â†’  InfoCircleOutlined,
    36â†’  NumberOutlined,
    37â†’  FontSizeOutlined
    38â†’} from '@ant-design/icons';
    39â†’import { api, FileData, Project, FileCheckResponse } from '../utils/api';
    40â†’
    41â†’const { Title, Text } = Typography;
    42â†’const { Option } = Select;
    43â†’const { Panel } = Collapse;
    44â†’
    45â†’interface FileStatus {
    46â†’  file: FileData;
    47â†’  status: 'uploaded' | 'embeddings_ready' | 'visualization_ready' | 'processing' | 'error';
    48â†’  hasVisualization: boolean;
    49â†’  hasEmbeddings: boolean;
    50â†’  error?: string;
    51â†’  fileInfo?: FileCheckResponse;
    52â†’}
    53â†’
    54â†’const FileManager: React.FC = () => {
    55â†’  const { projectId } = useParams<{ projectId: string }>();
    56â†’  const navigate = useNavigate();
    57â†’  
    58â†’  const [project, setProject] = useState<Project | null>(null);
    59â†’  const [fileStatuses, setFileStatuses] = useState<FileStatus[]>([]);
    60â†’  const [loading, setLoading] = useState(true);
    61â†’  const [error, setError] = useState<string | null>(null);
    62â†’  const [processingFile, setProcessingFile] = useState<number | null>(null);
    63â†’  const [generateModalVisible, setGenerateModalVisible] = useState(false);
    64â†’  const [selectedFile, setSelectedFile] = useState<FileData | null>(null);
    65â†’  const [selectedColumn, setSelectedColumn] = useState<string>('');
    66â†’  const [selectedModel, setSelectedModel] = useState<string>('openai-text-embedding-ada-002');
    67â†’  const [expandedCards, setExpandedCards] = useState<Set<number>>(new Set());
    68â†’
    69â†’  const toggleCardExpansion = (fileId: number) => {
    70â†’    const newExpanded = new Set(expandedCards);
    71â†’    if (newExpanded.has(fileId)) {
    72â†’      newExpanded.delete(fileId);
    73â†’    } else {
    74â†’      newExpanded.add(fileId);
    75â†’    }
    76â†’    setExpandedCards(newExpanded);
    77â†’  };
    78â†’
    79â†’  const getColumnTypeIcon = (columnName: string, fileData: FileData) => {
    80â†’    if (fileData.columns.numeric_columns.includes(columnName)) {
    81â†’      return <NumberOutlined style={{ color: '#1890ff' }} />;
    82â†’    } else if (fileData.columns.text_columns.includes(columnName)) {
    83â†’      return <FontSizeOutlined style={{ color: '#52c41a' }} />;
    84â†’    }
    85â†’    return <InfoCircleOutlined style={{ color: '#8c8c8c' }} />;
    86â†’  };
    87â†’
    88â†’  const getColumnTypeColor = (columnName: string, fileData: FileData) => {
    89â†’    if (fileData.columns.numeric_columns.includes(columnName)) {
    90â†’      return 'blue';
    91â†’    } else if (fileData.columns.text_columns.includes(columnName)) {
    92â†’      return 'green';
    93â†’    }
    94â†’    return 'default';
    95â†’  };
    96â†’
    97â†’  const renderColumnDetails = (fileData: FileData, fileInfo?: FileCheckResponse) => {
    98â†’    const allColumns = fileData.columns.names;
    99â†’    const textColumns = fileData.columns.text_columns;
   100â†’    const numericColumns = fileData.columns.numeric_columns;
   101â†’    
   102â†’    return (
   103â†’      <div className="mt-4">
   104â†’        <Divider orientation="left" orientationMargin="0">
   105â†’          <Text strong>Column Details</Text>
   106â†’        </Divider>
   107â†’        
   108â†’        <div className="mb-3">
   109â†’          <Text strong className="text-sm">Text Columns ({textColumns.length}):</Text>
   110â†’          <div className="mt-2 flex flex-wrap gap-1">
   111â†’            {textColumns.map(column => (
   112â†’              <Tag 
   113â†’                key={column} 
   114â†’                color={getColumnTypeColor(column, fileData)}
   115â†’                icon={getColumnTypeIcon(column, fileData)}
   116â†’                className="mb-1"
   117â†’              >
   118â†’                {column}
   119â†’              </Tag>
   120â†’            ))}
   121â†’          </div>
   122â†’        </div>
   123â†’        
   124â†’        {numericColumns.length > 0 && (
   125â†’          <div className="mb-3">
   126â†’            <Text strong className="text-sm">Numeric Columns ({numericColumns.length}):</Text>
   127â†’            <div className="mt-2 flex flex-wrap gap-1">
   128â†’              {numericColumns.map(column => (
   129â†’                <Tag 
   130â†’                  key={column} 
   131â†’                  color={getColumnTypeColor(column, fileData)}
   132â†’                  icon={getColumnTypeIcon(column, fileData)}
   133â†’                  className="mb-1"
   134â†’                >
   135â†’                  {column}
   136â†’                </Tag>
   137â†’              ))}
   138â†’            </div>
   139â†’          </div>
   140â†’        )}
   141â†’        
   142â†’        {fileInfo?.has_visualizations && (
   143â†’          <div className="mt-3">
   144â†’            <Alert
   145â†’              message="Visualization Available"
   146â†’              description={`This file has ${fileInfo.visualization_count} visualizations using ${fileInfo.available_methods.join(', ')} methods.`}
   147â†’              type="success"
   148â†’              size="small"
   149â†’              showIcon
   150â†’            />
   151â†’          </div>
   152â†’        )}
   153â†’      </div>
   154â†’    );
   155â†’  };
   156â†’
   157â†’  const fetchProject = async () => {
   158â†’    if (!projectId) return;
   159â†’    
   160â†’    const result = await api.getProjects();
   161â†’    if (!result.error && result.data) {
   162â†’      const foundProject = result.data.find(p => p.project_id === parseInt(projectId));
   163â†’      setProject(foundProject || null);
   164â†’    }
   165â†’  };
   166â†’
   167â†’  const fetchFileStatuses = async () => {
   168â†’    if (!projectId) return;
   169â†’    
   170â†’    setLoading(true);
   171â†’    const filesResult = await api.getProjectFiles(parseInt(projectId));
   172â†’    
   173â†’    if (filesResult.error) {
   174â†’      setError(filesResult.error);
   175â†’      setLoading(false);
   176â†’      return;
   177â†’    }
   178â†’
   179â†’    // Check status for each file
   180â†’    const statuses = await Promise.all(
   181â†’      filesResult.data.map(async (file): Promise<FileStatus> => {
   182â†’        const statusCheck = await api.checkFileProcessingStatus(file.file_id);
   183â†’        return {
   184â†’          file,
   185â†’          status: statusCheck.status,
   186â†’          hasVisualization: statusCheck.hasVisualization,
   187â†’          hasEmbeddings: statusCheck.hasEmbeddings,
   188â†’          error: statusCheck.error,
   189â†’          fileInfo: statusCheck.fileInfo
   190â†’        };
   191â†’      })
   192â†’    );
   193â†’
   194â†’    setFileStatuses(statuses);
   195â†’    setLoading(false);
   196â†’  };
   197â†’
   198â†’  useEffect(() => {
   199â†’    fetchProject();
   200â†’    fetchFileStatuses();
   201â†’  }, [projectId]);
   202â†’
   203â†’  const getStatusBadge = (status: FileStatus['status'], fileInfo?: FileCheckResponse) => {
   204â†’    switch (status) {
   205â†’      case 'uploaded':
   206â†’        return <Badge status="default" text="Uploaded" />;
   207â†’      case 'embeddings_ready':
   208â†’        return <Badge status="processing" text="Embeddings Ready" />;
   209â†’      case 'visualization_ready':
   210â†’        return (
   211â†’          <Space>
   212â†’            <Badge status="success" text="Visualization Ready" />
   213â†’            {fileInfo?.visualization_count && (
   214â†’              <Tag color="green" size="small">
   215â†’                {fileInfo.visualization_count} viz
   216â†’              </Tag>
   217â†’            )}
   218â†’          </Space>
   219â†’        );
   220â†’      case 'processing':
   221â†’        return <Badge status="processing" text="Processing..." />;
   222â†’      case 'error':
   223â†’        return <Badge status="error" text="Error" />;
   224â†’      default:
   225â†’        return <Badge status="default" text="Unknown" />;
   226â†’    }
   227â†’  };
   228â†’
   229â†’  const getStatusIcon = (status: FileStatus['status']) => {
   230â†’    switch (status) {
   231â†’      case 'uploaded':
   232â†’        return <FileOutlined style={{ color: '#8c8c8c' }} />;
   233â†’      case 'embeddings_ready':
   234â†’        return <DatabaseOutlined style={{ color: '#1890ff' }} />;
   235â†’      case 'visualization_ready':
   236â†’        return <CheckCircleOutlined style={{ color: '#52c41a' }} />;
   237â†’      case 'processing':
   238â†’        return <ClockCircleOutlined style={{ color: '#1890ff' }} className="animate-pulse" />;
   239â†’      case 'error':
   240â†’        return <ExclamationCircleOutlined style={{ color: '#ff4d4f' }} />;
   241â†’      default:
   242â†’        return <FileOutlined />;
   243â†’    }
   244â†’  };
   245â†’
   246â†’  const handleFileAction = async (fileStatus: FileStatus) => {
   247â†’    const { file, status } = fileStatus;
   248â†’    
   249â†’    switch (status) {
   250â†’      case 'visualization_ready':
   251â†’        // Navigate to visualization
   252â†’        navigate(`/embeddings/${file.file_id}`);
   253â†’        break;
   254â†’      case 'embeddings_ready':
   255â†’        // Generate visualization - this would need to be implemented
   256â†’        message.info('Visualization generation not yet implemented');
   257â†’        break;
   258â†’      case 'uploaded':
   259â†’        // Generate embeddings
   260â†’        setSelectedFile(file);
   261â†’        setGenerateModalVisible(true);
   262â†’        break;
   263â†’      case 'processing':
   264â†’        // Show progress - could navigate to progress page
   265â†’        message.info('File is currently being processed');
   266â†’        break;
   267â†’      case 'error':
   268â†’        // Show error details or retry
   269â†’        message.error(fileStatus.error || 'Unknown error occurred');
   270â†’        break;
   271â†’    }
   272â†’  };
   273â†’
   274â†’  const handleGenerateEmbeddings = async () => {
   275â†’    if (!selectedFile || !selectedColumn) return;
   276â†’    
   277â†’    setProcessingFile(selectedFile.file_id);
   278â†’    const result = await api.generateEmbeddings(selectedFile.file_id, selectedColumn, selectedModel);
   279â†’    
   280â†’    if (result.error) {
   281â†’      message.error(`Failed to generate embeddings: ${result.error}`);
   282â†’    } else {
   283â†’      message.success('Embedding generation started');
   284â†’      // Navigate to progress page
   285â†’      navigate(`/job/${result.data.job_id}/progress`);
   286â†’    }
   287â†’    
   288â†’    setProcessingFile(null);
   289â†’    setGenerateModalVisible(false);
   290â†’    setSelectedFile(null);
   291â†’    setSelectedColumn('');
   292â†’  };
   293â†’
   294â†’  const getActionButton = (fileStatus: FileStatus) => {
   295â†’    const { status } = fileStatus;
   296â†’    
   297â†’    switch (status) {
   298â†’      case 'visualization_ready':
   299â†’        return (
   300â†’          <Button 
   301â†’            type="primary" 
   302â†’            icon={<EyeOutlined />}
   303â†’            onClick={() => handleFileAction(fileStatus)}
   304â†’          >
   305â†’            View Visualization
   306â†’          </Button>
   307â†’        );
   308â†’      case 'embeddings_ready':
   309â†’        return (
   310â†’          <Button 
   311â†’            type="default" 
   312â†’            icon={<PlayCircleOutlined />}
   313â†’            onClick={() => handleFileAction(fileStatus)}
   314â†’          >
   315â†’            Create Visualization
   316â†’          </Button>
   317â†’        );
   318â†’      case 'uploaded':
   319â†’        return (
   320â†’          <Button 
   321â†’            type="primary" 
   322â†’            icon={<PlayCircleOutlined />}
   323â†’            onClick={() => handleFileAction(fileStatus)}
   324â†’          >
   325â†’            Generate Embeddings
   326â†’          </Button>
   327â†’        );
   328â†’      case 'processing':
   329â†’        return (
   330â†’          <Button 
   331â†’            disabled 
   332â†’            icon={<ClockCircleOutlined />}
   333â†’          >
   334â†’            Processing...
   335â†’          </Button>
   336â†’        );
   337â†’      case 'error':
   338â†’        return (
   339â†’          <Button 
   340â†’            danger 
   341â†’            icon={<ExclamationCircleOutlined />}
   342â†’            onClick={() => handleFileAction(fileStatus)}
   343â†’          >
   344â†’            View Error
   345â†’          </Button>
   346â†’        );
   347â†’      default:
   348â†’        return (
   349â†’          <Button disabled>
   350â†’            Unknown Status
   351â†’          </Button>
   352â†’        );
   353â†’    }
   354â†’  };
   355â†’
   356â†’  if (loading) {
   357â†’    return (
   358â†’      <div className="flex justify-center items-center min-h-screen">
   359â†’        <Spin size="large" />
   360â†’      </div>
   361â†’    );
   362â†’  }
   363â†’
   364â†’  return (
   365â†’    <div className="p-6 max-w-7xl mx-auto">
   366â†’      <div className="mb-8">
   367â†’        <div className="flex justify-between items-center mb-4">
   368â†’          <div>
   369â†’            <Button 
   370â†’              type="text" 
   371â†’              onClick={() => navigate('/')}
   372â†’              className="mb-2"
   373â†’            >
   374â†’              â† Back to Projects
   375â†’            </Button>
   376â†’            <Title level={2} className="m-0">
   377â†’              {project?.name || 'Project Files'}
   378â†’            </Title>
   379â†’          </div>
   380â†’          <Space>
   381â†’            <Button 
   382â†’              icon={<ReloadOutlined />}
   383â†’              onClick={fetchFileStatuses}
   384â†’            >
   385â†’              Refresh
   386â†’            </Button>
   387â†’            <Button 
   388â†’              type="primary" 
   389â†’              icon={<UploadOutlined />}
   390â†’              onClick={() => navigate(`/add-data/${projectId}`)}
   391â†’            >
   392â†’              Add Data
   393â†’            </Button>
   394â†’          </Space>
   395â†’        </div>
   396â†’        
   397â†’        {error && (
   398â†’          <Alert 
   399â†’            message="Error" 
   400â†’            description={error} 
   401â†’            type="error" 
   402â†’            closable 
   403â†’            onClose={() => setError(null)}
   404â†’            className="mb-4"
   405â†’          />
   406â†’        )}
   407â†’
   408â†’        <Text type="secondary">
   409â†’          {project?.description || 'Manage files and visualizations for this project'}
   410â†’        </Text>
   411â†’      </div>
   412â†’
   413â†’      {fileStatuses.length === 0 ? (
   414â†’        <Empty
   415â†’          image={Empty.PRESENTED_IMAGE_SIMPLE}
   416â†’          description="No files in this project yet"
   417â†’        >
   418â†’          <Button 
   419â†’            type="primary" 
   420â†’            icon={<UploadOutlined />}
   421â†’            onClick={() => navigate(`/add-data/${projectId}`)}
   422â†’          >
   423â†’            Upload Your First File
   424â†’          </Button>
   425â†’        </Empty>
   426â†’      ) : (
   427â†’        <Row gutter={[24, 24]}>
   428â†’          {fileStatuses.map((fileStatus) => (
   429â†’            <Col xs={24} sm={12} lg={8} key={fileStatus.file.file_id}>
   430â†’              <Card
   431â†’                hoverable
   432â†’                className="h-full"
   433â†’                title={
   434â†’                  <div className="flex items-center justify-between">
   435â†’                    <div className="flex items-center space-x-2">
   436â†’                      {getStatusIcon(fileStatus.status)}
   437â†’                      <span className="truncate">{fileStatus.file.original_filename}</span>
   438â†’                    </div>
   439â†’                  </div>
   440â†’                }
   441â†’                extra={getStatusBadge(fileStatus.status, fileStatus.fileInfo)}
   442â†’                actions={[
   443â†’                  <Button
   444â†’                    key="toggle"
   445â†’                    type="text"
   446â†’                    size="small"
   447â†’                    icon={expandedCards.has(fileStatus.file.file_id) ? <UpOutlined /> : <DownOutlined />}
   448â†’                    onClick={() => toggleCardExpansion(fileStatus.file.file_id)}
   449â†’                  >
   450â†’                    {expandedCards.has(fileStatus.file.file_id) ? 'Less' : 'More'}
   451â†’                  </Button>,
   452â†’                  getActionButton(fileStatus)
   453â†’                ]}
   454â†’              >
   455â†’                <Descriptions column={1} size="small">
   456â†’                  <Descriptions.Item label="Rows">
   457â†’                    {fileStatus.file.row_count.toLocaleString()}
   458â†’                  </Descriptions.Item>
   459â†’                  <Descriptions.Item label="Columns">
   460â†’                    <Space>
   461â†’                      <span>{fileStatus.file.columns.names.length} total</span>
   462â†’                      <Tag color="green" size="small">
   463â†’                        {fileStatus.file.columns.text_columns.length} text
   464â†’                      </Tag>
   465â†’                      <Tag color="blue" size="small">
   466â†’                        {fileStatus.file.columns.numeric_columns.length} numeric
   467â†’                      </Tag>
   468â†’                    </Space>
   469â†’                  </Descriptions.Item>
   470â†’                  {fileStatus.fileInfo?.has_visualizations && (
   471â†’                    <Descriptions.Item label="Visualizations">
   472â†’                      <Space>
   473â†’                        <span>{fileStatus.fileInfo.visualization_count}</span>
   474â†’                        {fileStatus.fileInfo.available_methods.map(method => (
   475â†’                          <Tag key={method} color="purple" size="small">{method.toUpperCase()}</Tag>
   476â†’                        ))}
   477â†’                      </Space>
   478â†’                    </Descriptions.Item>
   479â†’                  )}
   480â†’                  <Descriptions.Item label="Uploaded">
   481â†’                    {new Date(fileStatus.file.created_at).toLocaleDateString()}
   482â†’                  </Descriptions.Item>
   483â†’                </Descriptions>
   484â†’                
   485â†’                {expandedCards.has(fileStatus.file.file_id) && 
   486â†’                  renderColumnDetails(fileStatus.file, fileStatus.fileInfo)
   487â†’                }
   488â†’
   489â†’                {fileStatus.error && !expandedCards.has(fileStatus.file.file_id) && (
   490â†’                  <Alert 
   491â†’                    message={fileStatus.error} 
   492â†’                    type="error" 
   493â†’                    size="small" 
   494â†’                    className="mt-2"
   495â†’                  />
   496â†’                )}
   497â†’                
   498â†’                {fileStatus.error && expandedCards.has(fileStatus.file.file_id) && (
   499â†’                  <div className="mt-4">
   500â†’                    <Alert 
   501â†’                      message="Processing Error" 
   502â†’                      description={fileStatus.error} 
   503â†’                      type="error" 
   504â†’                      size="small" 
   505â†’                      showIcon
   506â†’                    />
   507â†’                  </div>
   508â†’                )}
   509â†’              </Card>
   510â†’            </Col>
   511â†’          ))}
   512â†’        </Row>
   513â†’      )}
   514â†’
   515â†’      {/* Generate Embeddings Modal */}
   516â†’      <Modal
   517â†’        title="Generate Embeddings"
   518â†’        open={generateModalVisible}
   519â†’        onCancel={() => {
   520â†’          setGenerateModalVisible(false);
   521â†’          setSelectedFile(null);
   522â†’          setSelectedColumn('');
   523â†’        }}
   524â†’        footer={[
   525â†’          <Button 
   526â†’            key="cancel"
   527â†’            onClick={() => {
   528â†’              setGenerateModalVisible(false);
   529â†’              setSelectedFile(null);
   530â†’              setSelectedColumn('');
   531â†’            }}
   532â†’          >
   533â†’            Cancel
   534â†’          </Button>,
   535â†’          <Button
   536â†’            key="generate"
   537â†’            type="primary"
   538â†’            loading={processingFile === selectedFile?.file_id}
   539â†’            disabled={!selectedColumn}
   540â†’            onClick={handleGenerateEmbeddings}
   541â†’          >
   542â†’            Generate Embeddings
   543â†’          </Button>
   544â†’        ]}
   545â†’      >
   546â†’        {selectedFile && (
   547â†’          <div>
   548â†’            <div className="mb-4">
   549â†’              <Text strong>File: </Text>
   550â†’              <Text>{selectedFile.original_filename}</Text>
   551â†’              <div className="mt-2">
   552â†’                <Text type="secondary" className="text-sm">
   553â†’                  {selectedFile.row_count.toLocaleString()} rows, {selectedFile.columns.names.length} columns
   554â†’                </Text>
   555â†’              </div>
   556â†’            </div>
   557â†’            
   558â†’            <div className="mb-4">
   559â†’              <Text strong>Available Text Columns ({selectedFile.columns.text_columns.length}):</Text>
   560â†’              <div className="mt-2 p-3 bg-gray-50 rounded">
   561â†’                <div className="flex flex-wrap gap-1">
   562â†’                  {selectedFile.columns.text_columns.map(column => (
   563â†’                    <Tag 
   564â†’                      key={column} 
   565â†’                      color={selectedColumn === column ? 'blue' : 'green'}
   566â†’                      style={{ cursor: 'pointer' }}
   567â†’                      onClick={() => setSelectedColumn(column)}
   568â†’                    >
   569â†’                      {column}
   570â†’                    </Tag>
   571â†’                  ))}
   572â†’                </div>
   573â†’              </div>
   574â†’              <Text type="secondary" className="text-xs mt-1">
   575â†’                Click on a column name to select it for embedding generation
   576â†’              </Text>
   577â†’            </div>
   578â†’            
   579â†’            <div className="mt-4">
   580â†’              <Text strong>Selected Column:</Text>
   581â†’              <Select
   582â†’                className="w-full mt-2"
   583â†’                placeholder="Choose a text column"
   584â†’                value={selectedColumn}
   585â†’                onChange={setSelectedColumn}
   586â†’                showSearch
   587â†’                optionFilterProp="children"
   588â†’              >
   589â†’                {selectedFile.columns.text_columns.map(column => (
   590â†’                  <Option key={column} value={column}>
   591â†’                    <Space>
   592â†’                      <FontSizeOutlined style={{ color: '#52c41a' }} />
   593â†’                      {column}
   594â†’                      <Text type="secondary" className="text-xs">
   595â†’                        ({selectedFile.columns.types[column] || 'text'})
   596â†’                      </Text>
   597â†’                    </Space>
   598â†’                  </Option>
   599â†’                ))}
   600â†’              </Select>
   601â†’            </div>
   602â†’
   603â†’            <div className="mt-4">
   604â†’              <Text strong>Model:</Text>
   605â†’              <Select
   606â†’                className="w-full mt-2"
   607â†’                value={selectedModel}
   608â†’                onChange={setSelectedModel}
   609â†’              >
   610â†’                <Option value="openai-text-embedding-ada-002">
   611â†’                  <Space>
   612â†’                    <DatabaseOutlined />
   613â†’                    OpenAI Text Embedding Ada-002
   614â†’                  </Space>
   615â†’                </Option>
   616â†’              </Select>
   617â†’              <Text type="secondary" className="text-xs mt-1">
   618â†’                This model will generate embeddings for the selected text column
   619â†’              </Text>
   620â†’            </div>
   621â†’          </div>
   622â†’        )}
   623â†’      </Modal>
   624â†’    </div>
   625â†’  );
   626â†’};
   627â†’
   628â†’export default FileManager;

<system-reminder>
