     1â†’import React, { useState, useEffect } from 'react';
     2â†’import { useParams, useNavigate } from 'react-router-dom';
     3â†’import {
     4â†’  Card,
     5â†’  Button,
     6â†’  Row,
     7â†’  Col,
     8â†’  Typography,
     9â†’  Spin,
    10â†’  Alert,
    11â†’  Tag,
    12â†’  Space,
    13â†’  Tooltip,
    14â†’  Empty,
    15â†’  Modal,
    16â†’  Select,
    17â†’  message,
    18â†’  Descriptions,
    19â†’  Badge,
    20â†’  Collapse,
    21â†’  Divider
    22â†’} from 'antd';
    23â†’import {
    24â†’  FileOutlined,
    25â†’  EyeOutlined,
    26â†’  PlayCircleOutlined,
    27â†’  CheckCircleOutlined,
    28â†’  ClockCircleOutlined,
    29â†’  ExclamationCircleOutlined,
    30â†’  ReloadOutlined,
    31â†’  UploadOutlined,
    32â†’  DatabaseOutlined,
    33â†’  DownOutlined,
    34â†’  UpOutlined,
    35â†’  InfoCircleOutlined,
    36â†’  NumberOutlined,
    37â†’  FontSizeOutlined
    38â†’} from '@ant-design/icons';
    39â†’import { api, FileData, Project, FileCheckResponse } from '../utils/api';
    40â†’
    41â†’const { Title, Text } = Typography;
    42â†’const { Option } = Select;
    43â†’const { Panel } = Collapse;
    44â†’
    45â†’interface FileStatus {
    46â†’  file: FileData;
    47â†’  status: 'uploaded' | 'embeddings_ready' | 'visualization_ready' | 'processing' | 'error';
    48â†’  hasVisualization: boolean;
    49â†’  hasEmbeddings: boolean;
    50â†’  error?: string;
    51â†’  fileInfo?: FileCheckResponse;
    52â†’}
    53â†’
    54â†’const FileManager: React.FC = () => {
    55â†’  const { projectId } = useParams<{ projectId: string }>();
    56â†’  const navigate = useNavigate();
    57â†’  
    58â†’  const [project, setProject] = useState<Project | null>(null);
    59â†’  const [fileStatuses, setFileStatuses] = useState<FileStatus[]>([]);
    60â†’  const [loading, setLoading] = useState(true);
    61â†’  const [error, setError] = useState<string | null>(null);
    62â†’  const [processingFile, setProcessingFile] = useState<number | null>(null);
    63â†’  const [generateModalVisible, setGenerateModalVisible] = useState(false);
    64â†’  const [selectedFile, setSelectedFile] = useState<FileData | null>(null);
    65â†’  const [selectedColumn, setSelectedColumn] = useState<string>('');
    66â†’  const [selectedModel, setSelectedModel] = useState<string>('openai-text-embedding-ada-002');
    67â†’  const [expandedCards, setExpandedCards] = useState<Set<number>>(new Set());
    68â†’
    69â†’  const toggleCardExpansion = (fileId: number) => {
    70â†’    const newExpanded = new Set(expandedCards);
    71â†’    if (newExpanded.has(fileId)) {
    72â†’      newExpanded.delete(fileId);
    73â†’    } else {
    74â†’      newExpanded.add(fileId);
    75â†’    }
    76â†’    setExpandedCards(newExpanded);
    77â†’  };
    78â†’
    79â†’  const getColumnTypeIcon = (columnName: string, fileData: FileData) => {
    80â†’    if (fileData.columns.numeric_columns.includes(columnName)) {
    81â†’      return <NumberOutlined style={{ color: '#1890ff' }} />;
    82â†’    } else if (fileData.columns.text_columns.includes(columnName)) {
    83â†’      return <FontSizeOutlined style={{ color: '#52c41a' }} />;
    84â†’    }
    85â†’    return <InfoCircleOutlined style={{ color: '#8c8c8c' }} />;
    86â†’  };
    87â†’
    88â†’  const getColumnTypeColor = (columnName: string, fileData: FileData) => {
    89â†’    if (fileData.columns.numeric_columns.includes(columnName)) {
    90â†’      return 'blue';
    91â†’    } else if (fileData.columns.text_columns.includes(columnName)) {
    92â†’      return 'green';
    93â†’    }
    94â†’    return 'default';
    95â†’  };
    96â†’
    97â†’  const renderColumnDetails = (fileData: FileData, fileInfo?: FileCheckResponse) => {
    98â†’    const allColumns = fileData.columns.names;
    99â†’    const textColumns = fileData.columns.text_columns;
   100â†’    const numericColumns = fileData.columns.numeric_columns;
   101â†’    
   102â†’    return (
   103â†’      <div className="mt-4">
   104â†’        <Divider orientation="left" orientationMargin="0">
   105â†’          <Text strong>Column Details</Text>
   106â†’        </Divider>
   107â†’        
   108â†’        <div className="mb-3">
   109â†’          <Text strong className="text-sm">Text Columns ({textColumns.length}):</Text>
   110â†’          <div className="mt-2 flex flex-wrap gap-1">
   111â†’            {textColumns.map(column => (
   112â†’              <Tag 
   113â†’                key={column} 
   114â†’                color={getColumnTypeColor(column, fileData)}
   115â†’                icon={getColumnTypeIcon(column, fileData)}
   116â†’                className="mb-1"
   117â†’              >
   118â†’                {column}
   119â†’              </Tag>
   120â†’            ))}
   121â†’          </div>
   122â†’        </div>
   123â†’        
   124â†’        {numericColumns.length > 0 && (
   125â†’          <div className="mb-3">
   126â†’            <Text strong className="text-sm">Numeric Columns ({numericColumns.length}):</Text>
   127â†’            <div className="mt-2 flex flex-wrap gap-1">
   128â†’              {numericColumns.map(column => (
   129â†’                <Tag 
   130â†’                  key={column} 
   131â†’                  color={getColumnTypeColor(column, fileData)}
   132â†’                  icon={getColumnTypeIcon(column, fileData)}
   133â†’                  className="mb-1"
   134â†’                >
   135â†’                  {column}
   136â†’                </Tag>
   137â†’              ))}
   138â†’            </div>
   139â†’          </div>
   140â†’        )}
   141â†’        
   142â†’        {fileInfo?.has_visualizations && (
   143â†’          <div className="mt-3">
   144â†’            <Alert
   145â†’              message="Visualization Available"
   146â†’              description={`This file has ${fileInfo.visualization_count} visualizations using ${fileInfo.available_methods.join(', ')} methods.`}
   147â†’              type="success"
   148â†’              size="small"
   149â†’              showIcon
   150â†’            />
   151â†’          </div>
   152â†’        )}
   153â†’      </div>
   154â†’    );
   155â†’  };
   156â†’
   157â†’  const fetchProject = async () => {
   158â†’    if (!projectId) return;
   159â†’    
   160â†’    const result = await api.getProjects();
   161â†’    if (!result.error && result.data) {
   162â†’      const foundProject = result.data.find(p => p.project_id === parseInt(projectId));
   163â†’      setProject(foundProject || null);
   164â†’    }
   165â†’  };
   166â†’
   167â†’  const fetchFileStatuses = async () => {
   168â†’    if (!projectId) return;
   169â†’    
   170â†’    setLoading(true);
   171â†’    const filesResult = await api.getProjectFiles(parseInt(projectId));
   172â†’    
   173â†’    if (filesResult.error) {
   174â†’      setError(filesResult.error);
   175â†’      setLoading(false);
   176â†’      return;
   177â†’    }
   178â†’
   179â†’    // Check status for each file
   180â†’    const statuses = await Promise.all(
   181â†’      filesResult.data.map(async (file): Promise<FileStatus> => {
   182â†’        const statusCheck = await api.checkFileProcessingStatus(file.file_id);
   183â†’        return {
   184â†’          file,
   185â†’          status: statusCheck.status,
   186â†’          hasVisualization: statusCheck.hasVisualization,
   187â†’          hasEmbeddings: statusCheck.hasEmbeddings,
   188â†’          error: statusCheck.error,
   189â†’          fileInfo: statusCheck.fileInfo
   190â†’        };
   191â†’      })
   192â†’    );
   193â†’
   194â†’    setFileStatuses(statuses);
   195â†’    setLoading(false);
   196â†’  };
   197â†’
   198â†’  useEffect(() => {
   199â†’    fetchProject();
   200â†’    fetchFileStatuses();
   201â†’  }, [projectId]);
   202â†’
   203â†’  const getStatusBadge = (status: FileStatus['status'], fileInfo?: FileCheckResponse) => {
   204â†’    switch (status) {
   205â†’      case 'uploaded':
   206â†’        return <Badge status="default" text="Uploaded" />;
   207â†’      case 'embeddings_ready':
   208â†’        return <Badge status="processing" text="Embeddings Ready" />;
   209â†’      case 'visualization_ready':
   210â†’        return (
   211â†’          <Space>
   212â†’            <Badge status="success" text="Visualization Ready" />
   213â†’            {fileInfo?.visualization_count && (
   214â†’              <Tag color="green" size="small">
   215â†’                {fileInfo.visualization_count} viz
   216â†’              </Tag>
   217â†’            )}
   218â†’          </Space>
   219â†’        );
   220â†’      case 'processing':
   221â†’        return <Badge status="processing" text="Processing..." />;
   222â†’      case 'error':
   223â†’        return <Badge status="error" text="Error" />;
   224â†’      default:
   225â†’        return <Badge status="default" text="Unknown" />;
   226â†’    }
   227â†’  };
   228â†’
   229â†’  const getStatusIcon = (status: FileStatus['status']) => {
   230â†’    switch (status) {
   231â†’      case 'uploaded':
   232â†’        return <FileOutlined style={{ color: '#8c8c8c' }} />;
   233â†’      case 'embeddings_ready':
   234â†’        return <DatabaseOutlined style={{ color: '#1890ff' }} />;
   235â†’      case 'visualization_ready':
   236â†’        return <CheckCircleOutlined style={{ color: '#52c41a' }} />;
   237â†’      case 'processing':
   238â†’        return <ClockCircleOutlined style={{ color: '#1890ff' }} className="animate-pulse" />;
   239â†’      case 'error':
   240â†’        return <ExclamationCircleOutlined style={{ color: '#ff4d4f' }} />;
   241â†’      default:
   242â†’        return <FileOutlined />;
   243â†’    }
   244â†’  };
   245â†’
   246â†’  const handleFileAction = async (fileStatus: FileStatus) => {
   247â†’    const { file, status } = fileStatus;
   248â†’    
   249â†’    switch (status) {
   250â†’      case 'visualization_ready':
   251â†’        // Navigate to visualization
   252â†’        navigate(`/embeddings/${file.file_id}`);
   253â†’        break;
