     1→import React, { useState, useEffect } from 'react';
     2→import { useParams, useNavigate } from 'react-router-dom';
     3→import {
     4→  Card,
     5→  Button,
     6→  Row,
     7→  Col,
     8→  Typography,
     9→  Spin,
    10→  Alert,
    11→  Tag,
    12→  Space,
    13→  Tooltip,
    14→  Empty,
    15→  Modal,
    16→  Select,
    17→  message,
    18→  Descriptions,
    19→  Badge,
    20→  Collapse,
    21→  Divider
    22→} from 'antd';
    23→import {
    24→  FileOutlined,
    25→  EyeOutlined,
    26→  PlayCircleOutlined,
    27→  CheckCircleOutlined,
    28→  ClockCircleOutlined,
    29→  ExclamationCircleOutlined,
    30→  ReloadOutlined,
    31→  UploadOutlined,
    32→  DatabaseOutlined,
    33→  DownOutlined,
    34→  UpOutlined,
    35→  InfoCircleOutlined,
    36→  NumberOutlined,
    37→  FontSizeOutlined
    38→} from '@ant-design/icons';
    39→import { api, FileData, Project, FileCheckResponse } from '../utils/api';
    40→
    41→const { Title, Text } = Typography;
    42→const { Option } = Select;
    43→const { Panel } = Collapse;
    44→
    45→interface FileStatus {
    46→  file: FileData;
    47→  status: 'uploaded' | 'embeddings_ready' | 'visualization_ready' | 'processing' | 'error';
    48→  hasVisualization: boolean;
    49→  hasEmbeddings: boolean;
    50→  error?: string;
    51→  fileInfo?: FileCheckResponse;
    52→}
    53→
    54→const FileManager: React.FC = () => {
    55→  const { projectId } = useParams<{ projectId: string }>();
    56→  const navigate = useNavigate();
    57→  
    58→  const [project, setProject] = useState<Project | null>(null);
    59→  const [fileStatuses, setFileStatuses] = useState<FileStatus[]>([]);
    60→  const [loading, setLoading] = useState(true);
    61→  const [error, setError] = useState<string | null>(null);
    62→  const [processingFile, setProcessingFile] = useState<number | null>(null);
    63→  const [generateModalVisible, setGenerateModalVisible] = useState(false);
    64→  const [selectedFile, setSelectedFile] = useState<FileData | null>(null);
    65→  const [selectedColumn, setSelectedColumn] = useState<string>('');
    66→  const [selectedModel, setSelectedModel] = useState<string>('openai-text-embedding-ada-002');
    67→  const [expandedCards, setExpandedCards] = useState<Set<number>>(new Set());
    68→
    69→  const toggleCardExpansion = (fileId: number) => {
    70→    const newExpanded = new Set(expandedCards);
    71→    if (newExpanded.has(fileId)) {
    72→      newExpanded.delete(fileId);
    73→    } else {
    74→      newExpanded.add(fileId);
    75→    }
    76→    setExpandedCards(newExpanded);
    77→  };
    78→
    79→  const getColumnTypeIcon = (columnName: string, fileData: FileData) => {
    80→    if (fileData.columns.numeric_columns.includes(columnName)) {
    81→      return <NumberOutlined style={{ color: '#1890ff' }} />;
    82→    } else if (fileData.columns.text_columns.includes(columnName)) {
    83→      return <FontSizeOutlined style={{ color: '#52c41a' }} />;
    84→    }
    85→    return <InfoCircleOutlined style={{ color: '#8c8c8c' }} />;
    86→  };
    87→
    88→  const getColumnTypeColor = (columnName: string, fileData: FileData) => {
    89→    if (fileData.columns.numeric_columns.includes(columnName)) {
    90→      return 'blue';
    91→    } else if (fileData.columns.text_columns.includes(columnName)) {
    92→      return 'green';
    93→    }
    94→    return 'default';
    95→  };
    96→
    97→  const renderColumnDetails = (fileData: FileData, fileInfo?: FileCheckResponse) => {
    98→    const allColumns = fileData.columns.names;
    99→    const textColumns = fileData.columns.text_columns;
   100→    const numericColumns = fileData.columns.numeric_columns;
   101→    
   102→    return (
   103→      <div className="mt-4">
   104→        <Divider orientation="left" orientationMargin="0">
   105→          <Text strong>Column Details</Text>
   106→        </Divider>
   107→        
   108→        <div className="mb-3">
   109→          <Text strong className="text-sm">Text Columns ({textColumns.length}):</Text>
   110→          <div className="mt-2 flex flex-wrap gap-1">
   111→            {textColumns.map(column => (
   112→              <Tag 
   113→                key={column} 
   114→                color={getColumnTypeColor(column, fileData)}
   115→                icon={getColumnTypeIcon(column, fileData)}
   116→                className="mb-1"
   117→              >
   118→                {column}
   119→              </Tag>
   120→            ))}
   121→          </div>
   122→        </div>
   123→        
   124→        {numericColumns.length > 0 && (
   125→          <div className="mb-3">
   126→            <Text strong className="text-sm">Numeric Columns ({numericColumns.length}):</Text>
   127→            <div className="mt-2 flex flex-wrap gap-1">
   128→              {numericColumns.map(column => (
   129→                <Tag 
   130→                  key={column} 
   131→                  color={getColumnTypeColor(column, fileData)}
   132→                  icon={getColumnTypeIcon(column, fileData)}
   133→                  className="mb-1"
   134→                >
   135→                  {column}
   136→                </Tag>
   137→              ))}
   138→            </div>
   139→          </div>
   140→        )}
   141→        
   142→        {fileInfo?.has_visualizations && (
   143→          <div className="mt-3">
   144→            <Alert
   145→              message="Visualization Available"
   146→              description={`This file has ${fileInfo.visualization_count} visualizations using ${fileInfo.available_methods.join(', ')} methods.`}
   147→              type="success"
   148→              size="small"
   149→              showIcon
   150→            />
   151→          </div>
   152→        )}
   153→      </div>
   154→    );
   155→  };
   156→
   157→  const fetchProject = async () => {
   158→    if (!projectId) return;
   159→    
   160→    const result = await api.getProjects();
   161→    if (!result.error && result.data) {
   162→      const foundProject = result.data.find(p => p.project_id === parseInt(projectId));
   163→      setProject(foundProject || null);
   164→    }
   165→  };
   166→
   167→  const fetchFileStatuses = async () => {
   168→    if (!projectId) return;
   169→    
   170→    setLoading(true);
   171→    const filesResult = await api.getProjectFiles(parseInt(projectId));
   172→    
   173→    if (filesResult.error) {
   174→      setError(filesResult.error);
   175→      setLoading(false);
   176→      return;
   177→    }
   178→
   179→    // Check status for each file
   180→    const statuses = await Promise.all(
   181→      filesResult.data.map(async (file): Promise<FileStatus> => {
   182→        const statusCheck = await api.checkFileProcessingStatus(file.file_id);
   183→        return {
   184→          file,
   185→          status: statusCheck.status,
   186→          hasVisualization: statusCheck.hasVisualization,
   187→          hasEmbeddings: statusCheck.hasEmbeddings,
   188→          error: statusCheck.error,
   189→          fileInfo: statusCheck.fileInfo
   190→        };
   191→      })
   192→    );
   193→
   194→    setFileStatuses(statuses);
   195→    setLoading(false);
   196→  };
   197→
   198→  useEffect(() => {
   199→    fetchProject();
   200→    fetchFileStatuses();
   201→  }, [projectId]);
   202→
   203→  const getStatusBadge = (status: FileStatus['status'], fileInfo?: FileCheckResponse) => {
   204→    switch (status) {
   205→      case 'uploaded':
   206→        return <Badge status="default" text="Uploaded" />;
   207→      case 'embeddings_ready':
   208→        return <Badge status="processing" text="Embeddings Ready" />;
   209→      case 'visualization_ready':
   210→        return (
   211→          <Space>
   212→            <Badge status="success" text="Visualization Ready" />
   213→            {fileInfo?.visualization_count && (
   214→              <Tag color="green" size="small">
   215→                {fileInfo.visualization_count} viz
   216→              </Tag>
   217→            )}
   218→          </Space>
   219→        );
   220→      case 'processing':
   221→        return <Badge status="processing" text="Processing..." />;
   222→      case 'error':
   223→        return <Badge status="error" text="Error" />;
   224→      default:
   225→        return <Badge status="default" text="Unknown" />;
   226→    }
   227→  };
   228→
   229→  const getStatusIcon = (status: FileStatus['status']) => {
   230→    switch (status) {
   231→      case 'uploaded':
   232→        return <FileOutlined style={{ color: '#8c8c8c' }} />;
   233→      case 'embeddings_ready':
   234→        return <DatabaseOutlined style={{ color: '#1890ff' }} />;
   235→      case 'visualization_ready':
   236→        return <CheckCircleOutlined style={{ color: '#52c41a' }} />;
   237→      case 'processing':
   238→        return <ClockCircleOutlined style={{ color: '#1890ff' }} className="animate-pulse" />;
   239→      case 'error':
   240→        return <ExclamationCircleOutlined style={{ color: '#ff4d4f' }} />;
   241→      default:
   242→        return <FileOutlined />;
   243→    }
   244→  };
   245→
   246→  const handleFileAction = async (fileStatus: FileStatus) => {
   247→    const { file, status } = fileStatus;
   248→    
   249→    switch (status) {
   250→      case 'visualization_ready':
   251→        // Navigate to visualization
   252→        navigate(`/embeddings/${file.file_id}`);
   253→        break;
   254→      case 'embeddings_ready':
   255→        // Generate visualization - this would need to be implemented
   256→        message.info('Visualization generation not yet implemented');
   257→        break;
   258→      case 'uploaded':
   259→        // Generate embeddings
   260→        setSelectedFile(file);
   261→        setGenerateModalVisible(true);
   262→        break;
   263→      case 'processing':
   264→        // Show progress - could navigate to progress page
   265→        message.info('File is currently being processed');
   266→        break;
   267→      case 'error':
   268→        // Show error details or retry
   269→        message.error(fileStatus.error || 'Unknown error occurred');
   270→        break;
   271→    }
   272→  };
   273→
   274→  const handleGenerateEmbeddings = async () => {
   275→    if (!selectedFile || !selectedColumn) return;
   276→    
   277→    setProcessingFile(selectedFile.file_id);
   278→    const result = await api.generateEmbeddings(selectedFile.file_id, selectedColumn, selectedModel);
   279→    
   280→    if (result.error) {
   281→      message.error(`Failed to generate embeddings: ${result.error}`);
   282→    } else {
   283→      message.success('Embedding generation started');
   284→      // Navigate to progress page
   285→      navigate(`/job/${result.data.job_id}/progress`);
   286→    }
   287→    
   288→    setProcessingFile(null);
   289→    setGenerateModalVisible(false);
   290→    setSelectedFile(null);
   291→    setSelectedColumn('');
   292→  };
   293→
   294→  const getActionButton = (fileStatus: FileStatus) => {
   295→    const { status } = fileStatus;
   296→    
   297→    switch (status) {
   298→      case 'visualization_ready':
   299→        return (
   300→          <Button 
   301→            type="primary" 
   302→            icon={<EyeOutlined />}
   303→            onClick={() => handleFileAction(fileStatus)}
   304→          >
   305→            View Visualization
   306→          </Button>
   307→        );
   308→      case 'embeddings_ready':
   309→        return (
   310→          <Button 
   311→            type="default" 
   312→            icon={<PlayCircleOutlined />}
   313→            onClick={() => handleFileAction(fileStatus)}
   314→          >
   315→            Create Visualization
   316→          </Button>
   317→        );
   318→      case 'uploaded':
   319→        return (
   320→          <Button 
   321→            type="primary" 
   322→            icon={<PlayCircleOutlined />}
   323→            onClick={() => handleFileAction(fileStatus)}
   324→          >
   325→            Generate Embeddings
   326→          </Button>
   327→        );
   328→      case 'processing':
   329→        return (
   330→          <Button 
   331→            disabled 
   332→            icon={<ClockCircleOutlined />}
   333→          >
   334→            Processing...
   335→          </Button>
   336→        );
   337→      case 'error':
   338→        return (
   339→          <Button 
   340→            danger 
   341→            icon={<ExclamationCircleOutlined />}
   342→            onClick={() => handleFileAction(fileStatus)}
   343→          >
   344→            View Error
   345→          </Button>
   346→        );
   347→      default:
   348→        return (
   349→          <Button disabled>
   350→            Unknown Status
   351→          </Button>
   352→        );
   353→    }
   354→  };
   355→
   356→  if (loading) {
   357→    return (
   358→      <div className="flex justify-center items-center min-h-screen">
   359→        <Spin size="large" />
   360→      </div>
   361→    );
   362→  }
   363→
   364→  return (
   365→    <div className="p-6 max-w-7xl mx-auto">
   366→      <div className="mb-8">
   367→        <div className="flex justify-between items-center mb-4">
   368→          <div>
   369→            <Button 
   370→              type="text" 
   371→              onClick={() => navigate('/')}
   372→              className="mb-2"
   373→            >
   374→              ← Back to Projects
   375→            </Button>
   376→            <Title level={2} className="m-0">
   377→              {project?.name || 'Project Files'}
   378→            </Title>
   379→          </div>
   380→          <Space>
   381→            <Button 
   382→              icon={<ReloadOutlined />}
   383→              onClick={fetchFileStatuses}
   384→            >
   385→              Refresh
   386→            </Button>
   387→            <Button 
   388→              type="primary" 
   389→              icon={<UploadOutlined />}
   390→              onClick={() => navigate(`/add-data/${projectId}`)}
   391→            >
   392→              Add Data
   393→            </Button>
   394→          </Space>
   395→        </div>
   396→        
   397→        {error && (
   398→          <Alert 
   399→            message="Error" 
   400→            description={error} 
   401→            type="error" 
   402→            closable 
   403→            onClose={() => setError(null)}
   404→            className="mb-4"
   405→          />
   406→        )}
   407→
   408→        <Text type="secondary">
   409→          {project?.description || 'Manage files and visualizations for this project'}
   410→        </Text>
   411→      </div>
   412→
   413→      {fileStatuses.length === 0 ? (
   414→        <Empty
   415→          image={Empty.PRESENTED_IMAGE_SIMPLE}
   416→          description="No files in this project yet"
   417→        >
   418→          <Button 
   419→            type="primary" 
   420→            icon={<UploadOutlined />}
   421→            onClick={() => navigate(`/add-data/${projectId}`)}
   422→          >
   423→            Upload Your First File
   424→          </Button>
   425→        </Empty>
   426→      ) : (
   427→        <Row gutter={[24, 24]}>
   428→          {fileStatuses.map((fileStatus) => (
   429→            <Col xs={24} sm={12} lg={8} key={fileStatus.file.file_id}>
   430→              <Card
   431→                hoverable
   432→                className="h-full"
   433→                title={
   434→                  <div className="flex items-center justify-between">
   435→                    <div className="flex items-center space-x-2">
   436→                      {getStatusIcon(fileStatus.status)}
   437→                      <span className="truncate">{fileStatus.file.original_filename}</span>
   438→                    </div>
   439→                  </div>
   440→                }
   441→                extra={getStatusBadge(fileStatus.status, fileStatus.fileInfo)}
   442→                actions={[
   443→                  <Button
   444→                    key="toggle"
   445→                    type="text"
   446→                    size="small"
   447→                    icon={expandedCards.has(fileStatus.file.file_id) ? <UpOutlined /> : <DownOutlined />}
   448→                    onClick={() => toggleCardExpansion(fileStatus.file.file_id)}
   449→                  >
   450→                    {expandedCards.has(fileStatus.file.file_id) ? 'Less' : 'More'}
   451→                  </Button>,
   452→                  getActionButton(fileStatus)
   453→                ]}
   454→              >
   455→                <Descriptions column={1} size="small">
   456→                  <Descriptions.Item label="Rows">
   457→                    {fileStatus.file.row_count.toLocaleString()}
   458→                  </Descriptions.Item>
   459→                  <Descriptions.Item label="Columns">
   460→                    <Space>
   461→                      <span>{fileStatus.file.columns.names.length} total</span>
   462→                      <Tag color="green" size="small">
   463→                        {fileStatus.file.columns.text_columns.length} text
   464→                      </Tag>
   465→                      <Tag color="blue" size="small">
   466→                        {fileStatus.file.columns.numeric_columns.length} numeric
   467→                      </Tag>
   468→                    </Space>
   469→                  </Descriptions.Item>
   470→                  {fileStatus.fileInfo?.has_visualizations && (
   471→                    <Descriptions.Item label="Visualizations">
   472→                      <Space>
   473→                        <span>{fileStatus.fileInfo.visualization_count}</span>
   474→                        {fileStatus.fileInfo.available_methods.map(method => (
   475→                          <Tag key={method} color="purple" size="small">{method.toUpperCase()}</Tag>
   476→                        ))}
   477→                      </Space>
   478→                    </Descriptions.Item>
   479→                  )}
   480→                  <Descriptions.Item label="Uploaded">
   481→                    {new Date(fileStatus.file.created_at).toLocaleDateString()}
   482→                  </Descriptions.Item>
   483→                </Descriptions>
   484→                
   485→                {expandedCards.has(fileStatus.file.file_id) && 
   486→                  renderColumnDetails(fileStatus.file, fileStatus.fileInfo)
   487→                }
   488→
   489→                {fileStatus.error && !expandedCards.has(fileStatus.file.file_id) && (
   490→                  <Alert 
   491→                    message={fileStatus.error} 
   492→                    type="error" 
   493→                    size="small" 
   494→                    className="mt-2"
   495→                  />
   496→                )}
   497→                
   498→                {fileStatus.error && expandedCards.has(fileStatus.file.file_id) && (
   499→                  <div className="mt-4">
   500→                    <Alert 
   501→                      message="Processing Error" 
   502→                      description={fileStatus.error} 
   503→                      type="error" 
   504→                      size="small" 
   505→                      showIcon
   506→                    />
   507→                  </div>
   508→                )}
   509→              </Card>
   510→            </Col>
   511→          ))}
   512→        </Row>
   513→      )}
   514→
   515→      {/* Generate Embeddings Modal */}
   516→      <Modal
   517→        title="Generate Embeddings"
   518→        open={generateModalVisible}
   519→        onCancel={() => {
   520→          setGenerateModalVisible(false);
   521→          setSelectedFile(null);
   522→          setSelectedColumn('');
   523→        }}
   524→        footer={[
   525→          <Button 
   526→            key="cancel"
   527→            onClick={() => {
   528→              setGenerateModalVisible(false);
   529→              setSelectedFile(null);
   530→              setSelectedColumn('');
   531→            }}
   532→          >
   533→            Cancel
   534→          </Button>,
   535→          <Button
   536→            key="generate"
   537→            type="primary"
   538→            loading={processingFile === selectedFile?.file_id}
   539→            disabled={!selectedColumn}
   540→            onClick={handleGenerateEmbeddings}
   541→          >
   542→            Generate Embeddings
   543→          </Button>
   544→        ]}
   545→      >
   546→        {selectedFile && (
   547→          <div>
   548→            <div className="mb-4">
   549→              <Text strong>File: </Text>
   550→              <Text>{selectedFile.original_filename}</Text>
   551→              <div className="mt-2">
   552→                <Text type="secondary" className="text-sm">
   553→                  {selectedFile.row_count.toLocaleString()} rows, {selectedFile.columns.names.length} columns
   554→                </Text>
   555→              </div>
   556→            </div>
   557→            
   558→            <div className="mb-4">
   559→              <Text strong>Available Text Columns ({selectedFile.columns.text_columns.length}):</Text>
   560→              <div className="mt-2 p-3 bg-gray-50 rounded">
   561→                <div className="flex flex-wrap gap-1">
   562→                  {selectedFile.columns.text_columns.map(column => (
   563→                    <Tag 
   564→                      key={column} 
   565→                      color={selectedColumn === column ? 'blue' : 'green'}
   566→                      style={{ cursor: 'pointer' }}
   567→                      onClick={() => setSelectedColumn(column)}
   568→                    >
   569→                      {column}
   570→                    </Tag>
   571→                  ))}
   572→                </div>
   573→              </div>
   574→              <Text type="secondary" className="text-xs mt-1">
   575→                Click on a column name to select it for embedding generation
   576→              </Text>
   577→            </div>
   578→            
   579→            <div className="mt-4">
   580→              <Text strong>Selected Column:</Text>
   581→              <Select
   582→                className="w-full mt-2"
   583→                placeholder="Choose a text column"
   584→                value={selectedColumn}
   585→                onChange={setSelectedColumn}
   586→                showSearch
   587→                optionFilterProp="children"
   588→              >
   589→                {selectedFile.columns.text_columns.map(column => (
   590→                  <Option key={column} value={column}>
   591→                    <Space>
   592→                      <FontSizeOutlined style={{ color: '#52c41a' }} />
   593→                      {column}
   594→                      <Text type="secondary" className="text-xs">
   595→                        ({selectedFile.columns.types[column] || 'text'})
   596→                      </Text>
   597→                    </Space>
   598→                  </Option>
   599→                ))}
   600→              </Select>
   601→            </div>
   602→
   603→            <div className="mt-4">
   604→              <Text strong>Model:</Text>
   605→              <Select
   606→                className="w-full mt-2"
   607→                value={selectedModel}
   608→                onChange={setSelectedModel}
   609→              >
   610→                <Option value="openai-text-embedding-ada-002">
   611→                  <Space>
   612→                    <DatabaseOutlined />
   613→                    OpenAI Text Embedding Ada-002
   614→                  </Space>
   615→                </Option>
   616→              </Select>
   617→              <Text type="secondary" className="text-xs mt-1">
   618→                This model will generate embeddings for the selected text column
   619→              </Text>
   620→            </div>
   621→          </div>
   622→        )}
   623→      </Modal>
   624→    </div>
   625→  );
   626→};
   627→
   628→export default FileManager;

