     1→import React, { useState, useEffect } from 'react';
     2→import { useParams, useNavigate } from 'react-router-dom';
     3→import {
     4→  Button,
     5→  Typography,
     6→  Spin,
     7→  Alert,
     8→  Tag,
     9→  Space,
    10→  Tooltip,
    11→  Empty,
    12→  Modal,
    13→  Select,
    14→  message,
    15→  Badge,
    16→  Divider,
    17→  List,
    18→  Avatar
    19→} from 'antd';
    20→import {
    21→  FileOutlined,
    22→  EyeOutlined,
    23→  PlayCircleOutlined,
    24→  CheckCircleOutlined,
    25→  ClockCircleOutlined,
    26→  ExclamationCircleOutlined,
    27→  ReloadOutlined,
    28→  UploadOutlined,
    29→  DatabaseOutlined,
    30→  DownOutlined,
    31→  UpOutlined,
    32→  InfoCircleOutlined,
    33→  NumberOutlined,
    34→  FontSizeOutlined
    35→} from '@ant-design/icons';
    36→import { api, FileData, Project, FileCheckResponse } from '../utils/api';
    37→
    38→const { Title, Text } = Typography;
    39→const { Option } = Select;
    40→const { Panel } = Collapse;
    41→
    42→interface FileStatus {
    43→  file: FileData;
    44→  status: 'uploaded' | 'embeddings_ready' | 'visualization_ready' | 'processing' | 'error';
    45→  hasVisualization: boolean;
    46→  hasEmbeddings: boolean;
    47→  error?: string;
    48→  fileInfo?: FileCheckResponse;
    49→}
    50→
    51→const FileManager: React.FC = () => {
    52→  const { projectId } = useParams<{ projectId: string }>();
    53→  const navigate = useNavigate();
    54→  
    55→  const [project, setProject] = useState<Project | null>(null);
    56→  const [fileStatuses, setFileStatuses] = useState<FileStatus[]>([]);
    57→  const [loading, setLoading] = useState(true);
    58→  const [error, setError] = useState<string | null>(null);
    59→  const [processingFile, setProcessingFile] = useState<number | null>(null);
    60→  const [generateModalVisible, setGenerateModalVisible] = useState(false);
    61→  const [selectedFile, setSelectedFile] = useState<FileData | null>(null);
    62→  const [selectedColumn, setSelectedColumn] = useState<string>('');
    63→  const [selectedModel, setSelectedModel] = useState<string>('openai-text-embedding-ada-002');
    64→  const [expandedCards, setExpandedCards] = useState<Set<number>>(new Set());
    65→
    66→  const toggleCardExpansion = (fileId: number) => {
    67→    const newExpanded = new Set(expandedCards);
    68→    if (newExpanded.has(fileId)) {
    69→      newExpanded.delete(fileId);
    70→    } else {
    71→      newExpanded.add(fileId);
    72→    }
    73→    setExpandedCards(newExpanded);
    74→  };
    75→
    76→  const getColumnTypeIcon = (columnName: string, fileData: FileData) => {
    77→    if (fileData.columns.numeric_columns.includes(columnName)) {
    78→      return <NumberOutlined style={{ color: '#1890ff' }} />;
    79→    } else if (fileData.columns.text_columns.includes(columnName)) {
    80→      return <FontSizeOutlined style={{ color: '#52c41a' }} />;
    81→    }
    82→    return <InfoCircleOutlined style={{ color: '#8c8c8c' }} />;
    83→  };
    84→
    85→  const getColumnTypeColor = (columnName: string, fileData: FileData) => {
    86→    if (fileData.columns.numeric_columns.includes(columnName)) {
    87→      return 'blue';
    88→    } else if (fileData.columns.text_columns.includes(columnName)) {
    89→      return 'green';
    90→    }
    91→    return 'default';
    92→  };
    93→
    94→  const renderColumnDetails = (fileData: FileData, fileInfo?: FileCheckResponse) => {
    95→    const allColumns = fileData.columns.names;
    96→    const textColumns = fileData.columns.text_columns;
    97→    const numericColumns = fileData.columns.numeric_columns;
    98→    
    99→    return (
   100→      <div className="mt-4">
   101→        <Divider orientation="left" orientationMargin="0">
   102→          <Text strong>Column Details</Text>
   103→        </Divider>
   104→        
   105→        <div className="mb-3">
   106→          <Text strong className="text-sm">Text Columns ({textColumns.length}):</Text>
   107→          <div className="mt-2 flex flex-wrap gap-1">
   108→            {textColumns.map(column => (
   109→              <Tag 
   110→                key={column} 
   111→                color={getColumnTypeColor(column, fileData)}
   112→                icon={getColumnTypeIcon(column, fileData)}
   113→                className="mb-1"
   114→              >
   115→                {column}
   116→              </Tag>
   117→            ))}
   118→          </div>
   119→        </div>
   120→        
   121→        {numericColumns.length > 0 && (
   122→          <div className="mb-3">
   123→            <Text strong className="text-sm">Numeric Columns ({numericColumns.length}):</Text>
   124→            <div className="mt-2 flex flex-wrap gap-1">
   125→              {numericColumns.map(column => (
   126→                <Tag 
   127→                  key={column} 
   128→                  color={getColumnTypeColor(column, fileData)}
   129→                  icon={getColumnTypeIcon(column, fileData)}
   130→                  className="mb-1"
   131→                >
   132→                  {column}
   133→                </Tag>
   134→              ))}
   135→            </div>
   136→          </div>
   137→        )}
   138→        
   139→        {fileInfo?.has_visualizations && (
   140→          <div className="mt-3">
   141→            <Alert
   142→              message="Visualization Available"
   143→              description={`This file has ${fileInfo.visualization_count} visualizations using ${fileInfo.available_methods.join(', ')} methods.`}
   144→              type="success"
   145→              size="small"
   146→              showIcon
   147→            />
   148→          </div>
   149→        )}
   150→      </div>
   151→    );
   152→  };
   153→
   154→  const fetchProject = async () => {
   155→    if (!projectId) return;
   156→    
   157→    const result = await api.getProjects();
   158→    if (!result.error && result.data) {
   159→      const foundProject = result.data.find(p => p.project_id === parseInt(projectId));
   160→      setProject(foundProject || null);
   161→    }
   162→  };
   163→
   164→  const fetchFileStatuses = async () => {
   165→    if (!projectId) return;
   166→    
   167→    setLoading(true);
   168→    const filesResult = await api.getProjectFiles(parseInt(projectId));
   169→    
   170→    if (filesResult.error) {
   171→      setError(filesResult.error);
   172→      setLoading(false);
   173→      return;
   174→    }
   175→
   176→    // Check status for each file
   177→    const statuses = await Promise.all(
   178→      filesResult.data.map(async (file): Promise<FileStatus> => {
   179→        const statusCheck = await api.checkFileProcessingStatus(file.file_id);
   180→        return {
   181→          file,
   182→          status: statusCheck.status,
   183→          hasVisualization: statusCheck.hasVisualization,
   184→          hasEmbeddings: statusCheck.hasEmbeddings,
   185→          error: statusCheck.error,
   186→          fileInfo: statusCheck.fileInfo
   187→        };
   188→      })
   189→    );
   190→
   191→    setFileStatuses(statuses);
   192→    setLoading(false);
   193→  };
   194→
   195→  useEffect(() => {
   196→    fetchProject();
   197→    fetchFileStatuses();
   198→  }, [projectId]);
   199→
   200→  const getStatusBadge = (status: FileStatus['status'], fileInfo?: FileCheckResponse) => {
   201→    switch (status) {
   202→      case 'uploaded':
   203→        return <Badge status="default" text="Uploaded" />;
   204→      case 'embeddings_ready':
   205→        return <Badge status="processing" text="Embeddings Ready" />;
   206→      case 'visualization_ready':
   207→        return (
   208→          <Space>
   209→            <Badge status="success" text="Visualization Ready" />
   210→            {fileInfo?.visualization_count && (
   211→              <Tag color="green" size="small">
   212→                {fileInfo.visualization_count} viz
   213→              </Tag>
   214→            )}
   215→          </Space>
   216→        );
   217→      case 'processing':
   218→        return <Badge status="processing" text="Processing..." />;
   219→      case 'error':
   220→        return <Badge status="error" text="Error" />;
   221→      default:
   222→        return <Badge status="default" text="Unknown" />;
   223→    }
   224→  };
   225→
   226→  const getStatusIcon = (status: FileStatus['status']) => {
   227→    switch (status) {
   228→      case 'uploaded':
   229→        return <FileOutlined style={{ color: '#8c8c8c' }} />;
   230→      case 'embeddings_ready':
   231→        return <DatabaseOutlined style={{ color: '#1890ff' }} />;
   232→      case 'visualization_ready':
   233→        return <CheckCircleOutlined style={{ color: '#52c41a' }} />;
   234→      case 'processing':
   235→        return <ClockCircleOutlined style={{ color: '#1890ff' }} className="animate-pulse" />;
   236→      case 'error':
   237→        return <ExclamationCircleOutlined style={{ color: '#ff4d4f' }} />;
   238→      default:
   239→        return <FileOutlined />;
   240→    }
   241→  };
   242→
   243→  const handleFileAction = async (fileStatus: FileStatus) => {
   244→    const { file, status } = fileStatus;
   245→    
   246→    switch (status) {
   247→      case 'visualization_ready':
   248→        // Navigate to visualization
   249→        navigate(`/embeddings/${file.file_id}`);
   250→        break;
   251→      case 'embeddings_ready':
   252→        // Generate visualization - this would need to be implemented
   253→        message.info('Visualization generation not yet implemented');
   254→        break;
   255→      case 'uploaded':
   256→        // Generate embeddings
   257→        setSelectedFile(file);
   258→        setGenerateModalVisible(true);
   259→        break;
   260→      case 'processing':
   261→        // Show progress - could navigate to progress page
   262→        message.info('File is currently being processed');
   263→        break;
   264→      case 'error':
   265→        // Show error details or retry
   266→        message.error(fileStatus.error || 'Unknown error occurred');
   267→        break;
   268→    }
   269→  };
   270→
   271→  const handleGenerateEmbeddings = async () => {
   272→    if (!selectedFile || !selectedColumn) return;
   273→    
   274→    setProcessingFile(selectedFile.file_id);
   275→    const result = await api.generateEmbeddings(selectedFile.file_id, selectedColumn, selectedModel);
   276→    
   277→    if (result.error) {
   278→      message.error(`Failed to generate embeddings: ${result.error}`);
   279→    } else {
   280→      message.success('Embedding generation started');
   281→      // Navigate to progress page
   282→      navigate(`/job/${result.data.job_id}/progress`);
   283→    }
   284→    
   285→    setProcessingFile(null);
   286→    setGenerateModalVisible(false);
   287→    setSelectedFile(null);
   288→    setSelectedColumn('');
   289→  };
   290→
   291→  const getActionButton = (fileStatus: FileStatus) => {
   292→    const { status } = fileStatus;
   293→    
   294→    switch (status) {
   295→      case 'visualization_ready':
   296→        return (
   297→          <Button 
   298→            type="primary" 
   299→            icon={<EyeOutlined />}
   300→            onClick={() => handleFileAction(fileStatus)}
   301→          >
   302→            View Visualization
   303→          </Button>
   304→        );
   305→      case 'embeddings_ready':
   306→        return (
   307→          <Button 
   308→            type="default" 
   309→            icon={<PlayCircleOutlined />}
   310→            onClick={() => handleFileAction(fileStatus)}
   311→          >
   312→            Create Visualization
   313→          </Button>
   314→        );
   315→      case 'uploaded':
   316→        return (
   317→          <Button 
   318→            type="primary" 
   319→            icon={<PlayCircleOutlined />}
   320→            onClick={() => handleFileAction(fileStatus)}
   321→          >
   322→            Generate Embeddings
   323→          </Button>
   324→        );
   325→      case 'processing':
   326→        return (
   327→          <Button 
   328→            disabled 
   329→            icon={<ClockCircleOutlined />}
   330→          >
   331→            Processing...
   332→          </Button>
   333→        );
   334→      case 'error':
   335→        return (
   336→          <Button 
   337→            danger 
   338→            icon={<ExclamationCircleOutlined />}
   339→            onClick={() => handleFileAction(fileStatus)}
   340→          >
   341→            View Error
   342→          </Button>
   343→        );
   344→      default:
   345→        return (
   346→          <Button disabled>
   347→            Unknown Status
   348→          </Button>
   349→        );
   350→    }
   351→  };
   352→
   353→  if (loading) {
   354→    return (
   355→      <div className="flex justify-center items-center min-h-screen">
   356→        <Spin size="large" />
   357→      </div>
   358→    );
   359→  }
   360→
   361→  return (
   362→    <div className="p-6 max-w-7xl mx-auto">
   363→      <div className="mb-8">
   364→        <div className="flex justify-between items-center mb-4">
   365→          <div>
   366→            <Button 
   367→              type="text" 
   368→              onClick={() => navigate('/')}
   369→              className="mb-2"
   370→            >
   371→              ← Back to Projects
   372→            </Button>
   373→            <Title level={2} className="m-0">
   374→              {project?.name || 'Project Files'}
   375→            </Title>
   376→          </div>
   377→          <Space>
   378→            <Button 
   379→              icon={<ReloadOutlined />}
   380→              onClick={fetchFileStatuses}
   381→            >
   382→              Refresh
   383→            </Button>
   384→            <Button 
   385→              type="primary" 
   386→              icon={<UploadOutlined />}
   387→              onClick={() => navigate(`/add-data/${projectId}`)}
   388→            >
   389→              Add Data
   390→            </Button>
   391→          </Space>
   392→        </div>
   393→        
   394→        {error && (
   395→          <Alert 
   396→            message="Error" 
   397→            description={error} 
   398→            type="error" 
   399→            closable 
   400→            onClose={() => setError(null)}
   401→            className="mb-4"
   402→          />
   403→        )}
   404→
   405→        <Text type="secondary">
   406→          {project?.description || 'Manage files and visualizations for this project'}
   407→        </Text>
   408→      </div>
   409→
   410→      {fileStatuses.length === 0 ? (
   411→        <Empty
   412→          image={Empty.PRESENTED_IMAGE_SIMPLE}
   413→          description="No files in this project yet"
   414→        >
   415→          <Button 
   416→            type="primary" 
   417→            icon={<UploadOutlined />}
   418→            onClick={() => navigate(`/add-data/${projectId}`)}
   419→          >
   420→            Upload Your First File
   421→          </Button>
   422→        </Empty>
   423→      ) : (
   424→        <List
   425→          itemLayout="horizontal"
   426→          dataSource={fileStatuses}
   427→          renderItem={(fileStatus) => (
   428→            <List.Item
   429→              key={fileStatus.file.file_id}
   430→              actions={[
   431→                <Button
   432→                  key="toggle"
   433→                  type="text"
   434→                  size="small"
   435→                  icon={expandedCards.has(fileStatus.file.file_id) ? <UpOutlined /> : <DownOutlined />}
   436→                  onClick={() => toggleCardExpansion(fileStatus.file.file_id)}
   437→                >
   438→                  {expandedCards.has(fileStatus.file.file_id) ? 'Less' : 'More'}
   439→                </Button>,
   440→                getActionButton(fileStatus)
   441→              ]}
   442→            >
   443→              <List.Item.Meta
   444→                avatar={
   445→                  <Avatar 
   446→                    icon={getStatusIcon(fileStatus.status)} 
   447→                    style={{ 
   448→                      backgroundColor: 
   449→                        fileStatus.status === 'visualization_ready' ? '#52c41a' :
   450→                        fileStatus.status === 'embeddings_ready' ? '#1890ff' :
   451→                        fileStatus.status === 'processing' ? '#faad14' :
   452→                        fileStatus.status === 'error' ? '#ff4d4f' : '#d9d9d9'
   453→                    }}
   454→                  />
   455→                }
   456→                title={
   457→                  <div className="flex items-center justify-between">
   458→                    <span className="truncate">{fileStatus.file.original_filename}</span>
   459→                    {getStatusBadge(fileStatus.status, fileStatus.fileInfo)}
   460→                  </div>
   461→                }
   462→                description={
   463→                  <div className="space-y-2">
   464→                    <div className="flex items-center space-x-4 text-sm">
   465→                      <span><strong>Rows:</strong> {fileStatus.file.row_count.toLocaleString()}</span>
   466→                      <span><strong>Columns:</strong> {fileStatus.file.columns.names.length} total</span>
   467→                      <Tag color="green" size="small">
   468→                        {fileStatus.file.columns.text_columns.length} text
   469→                      </Tag>
   470→                      <Tag color="blue" size="small">
   471→                        {fileStatus.file.columns.numeric_columns.length} numeric
   472→                      </Tag>
   473→                      {fileStatus.fileInfo?.has_visualizations && (
   474→                        <Tag color="purple" size="small">
   475→                          {fileStatus.fileInfo.visualization_count} viz
   476→                        </Tag>
   477→                      )}
   478→                    </div>
   479→                    <div className="text-xs text-gray-500">
   480→                      Uploaded: {new Date(fileStatus.file.created_at).toLocaleDateString()}
   481→                    </div>
   482→                    
   483→                    {fileStatus.error && !expandedCards.has(fileStatus.file.file_id) && (
   484→                      <Alert 
   485→                        message={fileStatus.error} 
   486→                        type="error" 
   487→                        size="small" 
   488→                        className="mt-2"
   489→                      />
   490→                    )}
   491→                    
   492→                    {expandedCards.has(fileStatus.file.file_id) && (
   493→                      <div className="mt-4 p-4 bg-gray-50 rounded">
   494→                        {renderColumnDetails(fileStatus.file, fileStatus.fileInfo)}
   495→                        
   496→                        {fileStatus.error && (
   497→                          <div className="mt-4">
   498→                            <Alert 
   499→                              message="Processing Error" 
   500→                              description={fileStatus.error} 
   501→                              type="error" 
   502→                              size="small" 
   503→                              showIcon
   504→                            />
   505→                          </div>
   506→                        )}
   507→                      </div>
   508→                    )}
   509→                  </div>
   510→                }
   511→              />
   512→            </List.Item>
   513→          )}
   514→        />
   515→      )}
   516→
   517→      {/* Generate Embeddings Modal */}
   518→      <Modal
   519→        title="Generate Embeddings"
   520→        open={generateModalVisible}
   521→        onCancel={() => {
   522→          setGenerateModalVisible(false);
   523→          setSelectedFile(null);
   524→          setSelectedColumn('');
   525→        }}
   526→        footer={[
   527→          <Button 
   528→            key="cancel"
   529→            onClick={() => {
   530→              setGenerateModalVisible(false);
   531→              setSelectedFile(null);
   532→              setSelectedColumn('');
   533→            }}
   534→          >
   535→            Cancel
   536→          </Button>,
   537→          <Button
   538→            key="generate"
   539→            type="primary"
   540→            loading={processingFile === selectedFile?.file_id}
   541→            disabled={!selectedColumn}
   542→            onClick={handleGenerateEmbeddings}
   543→          >
   544→            Generate Embeddings
   545→          </Button>
   546→        ]}
   547→      >
   548→        {selectedFile && (
   549→          <div>
   550→            <div className="mb-4">
   551→              <Text strong>File: </Text>
   552→              <Text>{selectedFile.original_filename}</Text>
   553→              <div className="mt-2">
   554→                <Text type="secondary" className="text-sm">
   555→                  {selectedFile.row_count.toLocaleString()} rows, {selectedFile.columns.names.length} columns
   556→                </Text>
   557→              </div>
   558→            </div>
   559→            
   560→            <div className="mb-4">
   561→              <Text strong>Available Text Columns ({selectedFile.columns.text_columns.length}):</Text>
   562→              <div className="mt-2 p-3 bg-gray-50 rounded">
   563→                <div className="flex flex-wrap gap-1">
   564→                  {selectedFile.columns.text_columns.map(column => (
   565→                    <Tag 
   566→                      key={column} 
   567→                      color={selectedColumn === column ? 'blue' : 'green'}
   568→                      style={{ cursor: 'pointer' }}
   569→                      onClick={() => setSelectedColumn(column)}
   570→                    >
   571→                      {column}
   572→                    </Tag>
   573→                  ))}
   574→                </div>
   575→              </div>
   576→              <Text type="secondary" className="text-xs mt-1">
   577→                Click on a column name to select it for embedding generation
   578→              </Text>
   579→            </div>
   580→            
   581→            <div className="mt-4">
   582→              <Text strong>Selected Column:</Text>
   583→              <Select
   584→                className="w-full mt-2"
   585→                placeholder="Choose a text column"
   586→                value={selectedColumn}
   587→                onChange={setSelectedColumn}
   588→                showSearch
   589→                optionFilterProp="children"
   590→              >
   591→                {selectedFile.columns.text_columns.map(column => (
   592→                  <Option key={column} value={column}>
   593→                    <Space>
   594→                      <FontSizeOutlined style={{ color: '#52c41a' }} />
   595→                      {column}
   596→                      <Text type="secondary" className="text-xs">
   597→                        ({selectedFile.columns.types[column] || 'text'})
   598→                      </Text>
   599→                    </Space>
   600→                  </Option>
   601→                ))}
   602→              </Select>
   603→            </div>
   604→
   605→            <div className="mt-4">
   606→              <Text strong>Model:</Text>
   607→              <Select
   608→                className="w-full mt-2"
   609→                value={selectedModel}
   610→                onChange={setSelectedModel}
   611→              >
   612→                <Option value="openai-text-embedding-ada-002">
   613→                  <Space>
   614→                    <DatabaseOutlined />
   615→                    OpenAI Text Embedding Ada-002
   616→                  </Space>
   617→                </Option>
   618→              </Select>
   619→              <Text type="secondary" className="text-xs mt-1">
   620→                This model will generate embeddings for the selected text column
   621→              </Text>
   622→            </div>
   623→          </div>
   624→        )}
   625→      </Modal>
   626→    </div>
   627→  );
   628→};
   629→
   630→export default FileManager;

