     1→import React from 'react';
     2→import { Routes, Route, useParams } from 'react-router-dom';
     3→import { Layout } from 'antd';
     4→import EmbeddingVisualization from './components/EmbeddingVisualization';
     5→// import VietnamMap from './components/VietnamMap';
     6→import { AddDataWizard } from './components/AddDataWizard';
     7→import './App.css';
     8→import JobProgressTracker from './components/JobProgressTracker';
     9→import ProjectDashboard from './components/main';
    10→
    11→const { Content } = Layout;
    12→
    13→// Wrapper component to pass jobId from URL params to JobProgressTracker
    14→const JobProgressTrackerWrapper: React.FC = () => {
    15→  const { jobId } = useParams<{ jobId: string }>();
    16→  // Ensure jobId is not undefined before passing, though route definition implies it will exist
    17→  return <JobProgressTracker jobId={jobId || ''} />;
    18→};
    19→
    20→const App: React.FC = () => {
    21→  return (
    22→    <Layout>
    23→      <Content style={{ padding: '24px', minHeight: '100vh' }}>
    24→        <Routes>
    25→          <Route path="/embeddings/:embeddingId" element={<EmbeddingVisualization />} />
    26→          <Route path="/add-data" element={<AddDataWizard projectId={1} onClose={() => console.log("Wizard closed")} />} />
    27→           <Route
    28→            path="/job/:jobId/progress"
    29→            element={<JobProgressTrackerWrapper />}
    30→          />
    31→        </Routes>
    32→        <ProjectDashboard />
    33→      </Content>
    34→    </Layout>
    35→  );
    36→};
    37→
    38→export default App;
    39→

