   572→                  style={{ width: '100%' }}
   573→                />
   574→              </Space>
   575→              <Space direction="vertical" style={{ width: '100%'}}>
   576→                <Text strong>Dimensions:</Text>
   577→                <Radio.Group
   578→                  options={DIMENSIONS}
   579→                  onChange={(e) => setSelectedDimensions(e.target.value)}
   580→                  value={selectedDimensions}
   581→                  optionType="button"
   582→                  buttonStyle="solid"
   583→                  disabled={isLoadingData}
   584→                  style={{ width: '100%' }}
   585→                />
   586→              </Space>
   587→              <Space direction="vertical" style={{ width: '100%'}}>
   588→                <Text strong>Filter Clusters:</Text>
   589→                <Select
   590→                  mode="multiple"
   591→                  allowClear
   592→                  placeholder="All Clusters"
   593→                  style={{ width: '100%' }}
   594→                  onChange={handleFilterChange}
   595→                  options={uniqueClusters.map((cluster) => ({
   596→                      label: `Cluster ${cluster}`,
   597→                      value: cluster,
   598→                  }))}
   599→                  disabled={isLoadingData || points.length === 0}
   600→                  loading={isLoadingData}
   601→                />
   602→              </Space>
   603→              <Space direction="vertical" style={{ width: '100%'}}>
   604→                <Text strong>Export Data:</Text>
   605→                <Button
   606→                  icon={<DownloadOutlined />}
   607→                  onClick={() => handleExport('csv')}
   608→                  disabled={isLoadingData || !visualizationData || visualizationData.length === 0}
   609→                  style={{ width: '100%', marginTop: '5px' }}
   610→                >
   611→                  Export CSV
   612→                </Button>
   613→                <Button
   614→                  icon={<DownloadOutlined />}
   615→                  onClick={() => handleExport('json')}
   616→                  disabled={isLoadingData || !visualizationData || visualizationData.length === 0}
   617→                  style={{ width: '100%', marginTop: '5px' }}
   618→                >
   619→                  Export JSON
   620→                </Button>
   621→              </Space>
   622→            </Space>
   623→          </div>
   624→        )}
   625→
   626→        {/* Main Content Area (Plot / Progress / Loading / Error) */}
   627→        <div className="main-content-area" style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
   628→          {isProcessing ? renderProgress() : renderContent()}
   629→        </div>
   630→      </div>
   631→    </div>
   632→  );
   633→};
   634→
   635→export default EmbeddingVisualization;

