     1→import React, { useEffect, useState, useRef, useCallback } from 'react';
     2→import { Scatterplot } from 'deepscatter';
     3→import { Select, Radio, Spin, Button, Progress, Alert, Space, Typography } from 'antd';
     4→import { DownloadOutlined } from '@ant-design/icons';
     5→import { useParams } from 'react-router-dom';
     6→import { tableFromArrays } from 'apache-arrow';
     7→import './tooltip.css'
     8→
     9→const { Title, Text } = Typography;
    10→
    11→// --- Constants ---
    12→const CHART_PARENT_ID = 'deep-scatter-parent-element-id';
    13→const POLLING_INTERVAL_MS = 2000;
    14→const PLOT_INIT_DELAY_MS = 100; // Delay to potentially allow container dimensions to stabilize
    15→
    16→const METHODS = [
    17→  { label: 'UMAP', value: 'umap' },
    18→  { label: 'PCA', value: 'pca' },
    19→];
    20→const DIMENSIONS = [
    21→  { label: '2D', value: 2 },
    22→  { label: '3D', value: 3 },
    23→];
    24→
    25→// --- Interfaces ---
    26→interface Point {
    27→  x: number;
    28→  y: number;
    29→  z?: number;
    30→  cluster: number;
    31→}
    32→
    33→interface VisualizationData {
    34→  method: string;
    35→  dimensions: number;
    36→  visualization_id: number;
    37→  embedding_id: number;
    38→  file_id: number;
    39→  row_id: number;
    40→  coordinates: number[];
    41→  clusters: number;
    42→  created_at: string;
    43→}
    44→
    45→interface ProgressInfo {
    46→  status: 'pending' | 'processing' | 'complete' | 'failed';
    47→  progress: number;
    48→  current_step?: string;
    49→  error?: {
    50→    error: string;
    51→    traceback?: string; // Make traceback optional as it might not always be present
    52→  };
    53→}
    54→
    55→interface HoveredPoint extends Point {
    56→  metadata?: Record<string, any>;
    57→  title?: string;
    58→  id?: string | number;
    59→}
    60→
    61→// --- Helper Functions ---
    62→const transformVisualizationToPoints = (vis: VisualizationData[]): Point[] => {
    63→  if (!vis || !Array.isArray(vis)) {
    64→    return [];
    65→  }
    66→  
    67→  return vis.map(item => ({
    68→    x: item.coordinates[0],
    69→    y: item.coordinates[1],
    70→    ...(item.coordinates.length > 2 && { z: item.coordinates[2] }),
    71→    cluster: item.clusters
    72→  }));
    73→};
    74→
    75→// --- Component ---
    76→const EmbeddingVisualization: React.FC = () => {
    77→  const { embeddingId } = useParams<{ embeddingId: string }>();
    78→  const chartParentId = 'deep-scatter-parent-element-id';
    79→
    80→  // --- State ---
    81→  const [hoveredPoint, setHoveredPoint] = useState<HoveredPoint | null>(null);
    82→  const [visualizationData, setVisualizationData] = useState<VisualizationData[]>([]);
    83→  const [points, setPoints] = useState<Point[]>([]);
    84→  const [filteredPoints, setFilteredPoints] = useState<Point[]>([]);
    85→  const [uniqueClusters, setUniqueClusters] = useState<number[]>([]);
    86→
    87→  const [selectedMethod, setSelectedMethod] = useState<string>(METHODS[0].value);
    88→  const [selectedDimensions, setSelectedDimensions] = useState<number>(DIMENSIONS[0].value);
    89→
    90→  const [isLoadingData, setIsLoadingData] = useState(true);
    91→  const [isProcessing, setIsProcessing] = useState(false); // For backend processing state
    92→  const [progress, setProgress] = useState<ProgressInfo | null>(null);
    93→  const [error, setError] = useState<string | null>(null);
    94→
    95→  // --- Refs ---
    96→  const plotContainerRef = useRef<HTMLDivElement>(null);
    97→  const scatterplotInstanceRef = useRef<Scatterplot | null>(null);
    98→
    99→  // --- Callbacks ---
   100→  const handleFilterChange = useCallback((selectedClusterValues: number[]) => {
   101→    if (selectedClusterValues.length === 0) {
   102→      setFilteredPoints(points);
   103→    } else {
   104→      setFilteredPoints(points.filter(p => selectedClusterValues.includes(p.cluster)));
   105→    }
   106→  }, [points]);
   107→
   108→  const handleExport = useCallback(async (format: 'csv' | 'json') => {
   109→    if (!visualizationData?.visualization_id) {
   110→      setError("Cannot export: No visualization data available or visualization ID missing.");
   111→      return;
   112→    }
   113→    setError(null); // Clear previous errors
   114→    const vizId = visualizationData.visualization_id;
   115→    const token = localStorage.getItem('token');
   116→    const url = `/api/visualizations/${vizId}/export?format=${format}`;
   117→
   118→    try {
   119→      const response = await fetch(url, {
   120→        headers: { Authorization: `Bearer ${token}` },
   121→      });
   122→
   123→      if (!response.ok) {
   124→        throw new Error(`Export failed: ${response.status} ${response.statusText}`);
   125→      }
   126→
   127→      // Browser should handle the download based on Content-Disposition header from the API
   128→      const blob = await response.blob();
   129→      const downloadUrl = window.URL.createObjectURL(blob);
   130→      const link = document.createElement('a');
   131→      link.href = downloadUrl;
   132→      // Extract filename from header or generate one
   133→      const disposition = response.headers.get('content-disposition');
   134→      let filename = `visualization-${vizId}.${format}`;
   135→      if (disposition && disposition.indexOf('attachment') !== -1) {
   136→          const filenameRegex = /filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/;
   137→          const matches = filenameRegex.exec(disposition);
   138→          if (matches != null && matches[1]) {
   139→            filename = matches[1].replace(/['"]/g, '');
   140→          }
   141→      }
   142→      link.setAttribute('download', filename);
   143→      document.body.appendChild(link);
   144→      link.click();
   145→      link.remove();
   146→      window.URL.revokeObjectURL(downloadUrl);
   147→
   148→    } catch (err) {
   149→      console.error('Export error:', err);
   150→      setError(err instanceof Error ? err.message : 'An unknown error occurred during export.');
   151→    }
   152→  }, [visualizationData]);
   153→
   154→  // --- Effects ---
   155→
   156→  // Effect: Poll for Processing Progress
   157→  useEffect(() => {
   158→    if (!embeddingId) return;
   159→
   160→    let pollTimeoutId: NodeJS.Timeout | null = null;
   161→    let isCancelled = false;
   162→
   163→    const pollProgress = async () => {
   164→      if (isCancelled) return;
   165→
   166→      const token = localStorage.getItem('token');
   167→      try {
   168→        const response = await fetch(`/api/embeddings/${embeddingId}/progress`, {
   169→          headers: { Authorization: `Bearer ${token}` },
   170→        });
   171→
   172→        if (isCancelled) return; // Check again after await
   173→
   174→        if (!response.ok) {
   175→          // Don't poll again if the progress endpoint itself fails, unless it's a transient error
   176→          if (response.status !== 404) { // Example: Treat 404 as "not started" or "complete", stop polling
   177→             console.error(`Progress fetch failed: ${response.status}`);
   178→          }
   179→           setProgress(null); // Reset progress on error
   180→           setIsProcessing(false);
   181→           return; // Stop polling on error
   182→        }
   183→
   184→        const progressData: ProgressInfo = await response.json();
   185→
   186→        if (isCancelled) return;
   187→
   188→        setProgress(progressData);
   189→        setIsProcessing(progressData.status === 'processing' || progressData.status === 'pending');
   190→
   191→        if (progressData.status === 'processing' || progressData.status === 'pending') {
   192→          pollTimeoutId = setTimeout(pollProgress, POLLING_INTERVAL_MS);
   193→        } else {
   194→            setIsProcessing(false); // Ensure processing stops on complete/fail
   195→        }
   196→
   197→      } catch (error) {
   198→        if (!isCancelled) {
   199→          console.error('Error fetching progress:', error);
   200→          setError('Failed to fetch processing progress.');
   201→          setIsProcessing(false);
   202→          // Optionally stop polling on error, or implement retry logic
   203→        }
   204→      }
   205→    };
   206→
   207→    // Start polling immediately only if not already completed/failed from previous state
   208→    if (!progress || progress.status === 'pending' || progress.status === 'processing') {
   209→       setIsProcessing(true);
   210→       pollProgress();
   211→    } else {
   212→       setIsProcessing(false);
   213→    }
   214→
   215→
   216→    return () => {
   217→      isCancelled = true;
   218→      if (pollTimeoutId) {
   219→        clearTimeout(pollTimeoutId);
   220→      }
   221→    };
   222→  }, [embeddingId, progress?.status]); // Re-run if embeddingId changes, or status necessitates restarting polling logic
   223→
   224→
   225→  // Effect: Fetch Visualization Data when parameters change or processing completes
   226→  useEffect(() => {
   227→    if (!embeddingId || isProcessing) {
   228→      // Don't fetch if processing or no ID
   229→      if (!isProcessing) setIsLoadingData(false); // Not processing, but no ID, so stop loading indicator
   230→      return;
   231→    }
   232→
   233→    setIsLoadingData(true);
