   551→            className="controls-sidebar" 
   552→            style={{ 
   553→              width: '280px', // Fixed width for the sidebar
   554→              padding: '15px',
   555→              border: '1px solid #ddd',
   556→              borderRadius: '4px',
   557→              background: '#fdfdfd', // Slightly different background
   558→              height: 'fit-content' // Adjust height to content
   559→            }}
   560→          >
   561→            <Typography.Title level={4} style={{ marginTop: 0, marginBottom: '20px' }}>Controls</Typography.Title>
   562→            <Space direction="vertical" style={{ width: '100%'}}>
