     1→import React, { useState } from 'react';
     2→import { Steps, Card, Typography, message, Spin } from 'antd';
     3→import SelectSourceStep from './SelectSourceStep';
     4→import PreviewFieldsStep from './PreviewFieldsStep';
     5→import ReviewStep from './ReviewStep';
     6→import DoneStep from './DoneStep';
     7→import { UploadedFile, FileColumnInfo, EmbeddingSelection } from './types';
     8→// Assuming you have an API client setup
     9→
    10→const { Step } = Steps;
    11→const { Title } = Typography;
    12→
    13→// Define Project Type if not already globally available
    14→// interface Project {
    15→//   project_id: number;
    16→//   name: string;
    17→//   // ... other project fields
    18→// }
    19→
    20→// Props for the Wizard, e.g., the project it's associated with
    21→interface AddDataWizardProps {
    22→  projectId: number; // Assuming the wizard is for a specific project
    23→  onClose: () => void; // Function to close the wizard
    24→}
    25→
    26→// Define the expected structure of the backend file response after upload
    27→interface BackendFileResponse {
    28→  file_id: number;
    29→  filename: string;
    30→  original_filename: string;
    31→  file_type: string;
    32→  project_id: number;
    33→  columns: FileColumnInfo; // This is key for PreviewFieldsStep
    34→  created_at: string;
    35→  row_count: number;
    36→}
    37→
    38→const AddDataWizard: React.FC<AddDataWizardProps> = ({ projectId, onClose }) => {
    39→  const [currentStep, setCurrentStep] = useState(0);
    40→  const [uploadedFile, setUploadedFile] = useState<UploadedFile | null>(null);
    41→  const [fileId, setFileId] = useState<number | null>(null);
    42→  const [fileColumnInfo, setFileColumnInfo] = useState<FileColumnInfo | null>(null);
    43→  const [embeddingSelection, setEmbeddingSelection] = useState<EmbeddingSelection>({
    44→    selectedColumn: '', // Initialize with empty or a sensible default if possible
    45→    modelName: 'openai-text-embedding-ada-002', // Default model
    46→  });
    47→  const [jobId, setJobId] = useState<string | null>(null);
    48→  const [isLoading, setIsLoading] = useState(false);
    49→  const [loadingMessage, setLoadingMessage] = useState('Processing...');
    50→
    51→  // Define API functions (these would typically be in a service file)
    52→  const uploadFileApi = async (file: File, projId: number): Promise<BackendFileResponse> => {
    53→    const formData = new FormData();
    54→    formData.append('file', file);
    55→    setLoadingMessage('Uploading file...');
    56→    const token = localStorage.getItem('token');
    57→    const response = await fetch(`/api/files/upload/${projId}`, { // Assuming /api is your base path
    58→      method: 'POST',
    59→      body: formData,
    60→      headers: { Authorization: `Bearer ${token}` }, // Fetch sets Content-Type automatically for FormData
    61→    });
    62→    if (!response.ok) {
    63→      const errorData = await response.json().catch(() => ({ detail: response.statusText })); // Try to parse JSON, fallback to statusText
    64→      throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
    65→    }
    66→    return response.json();
    67→  };
    68→
    69→  const triggerEmbeddingApi = async (fileIdVal: number, column: string, modelName: string): Promise<{ job_id: string }> => {
    70→    setLoadingMessage('Starting embedding process...');
    71→    const token = localStorage.getItem('token');
    72→    const response = await fetch(`/api/embeddings/${fileIdVal}/generate?column=${encodeURIComponent(column)}&model_name=${encodeURIComponent(modelName)}`, { // Assuming /api is your base path
    73→      method: 'POST',
    74→      headers: {
    75→        'Content-Type': 'application/json', // Explicitly set Content-Type for JSON payload if any (not needed for GET usually but good for POST)
    76→        Authorization: `Bearer ${token}`,
    77→      },
    78→      // body: JSON.stringify({}), // If you were sending a JSON body
    79→    });
    80→    if (!response.ok) {
    81→      const errorData = await response.json().catch(() => ({ detail: response.statusText }));
    82→      throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
    83→    }
    84→    return response.json();
    85→  };
    86→
    87→  const handleFileSelectedAndUpload = async () => {
    88→    if (!uploadedFile) {
    89→      message.error('No file selected.');
    90→      return;
    91→    }
    92→    setIsLoading(true);
    93→    try {
    94→      const backendFileResponse = await uploadFileApi(uploadedFile.file, projectId);
    95→      setFileId(backendFileResponse.file_id);
    96→      setFileColumnInfo(backendFileResponse.columns);
    97→      message.success(`${uploadedFile.file.name} uploaded successfully. File ID: ${backendFileResponse.file_id}`);
    98→      // Automatically select the first text column if available
    99→      if (backendFileResponse.columns?.text_columns?.length > 0) {
   100→        setEmbeddingSelection(prev => ({ ...prev, selectedColumn: backendFileResponse.columns.text_columns[0] }));
   101→      }
   102→      setCurrentStep(currentStep + 1); // Move to PreviewFieldsStep
   103→    } catch (err: any) {
   104→      console.error('Error during file upload step:', err);
   105→      message.error(`File upload failed: ${err.message}`); // err.response?.data?.detail removed as fetch doesn't have response.data
   106→    } finally {
   107→      setIsLoading(false);
   108→    }
   109→  };
   110→  
   111→  const handlePreviewAndFieldsNext = (selection: EmbeddingSelection) => {
   112→    setEmbeddingSelection(selection);
   113→    setCurrentStep(currentStep + 1); // Move to ReviewStep
   114→  };
   115→
   116→  const handleReviewAndTriggerEmbedding = async () => {
   117→    if (!fileId || !embeddingSelection.selectedColumn) {
   118→      message.error('File ID or embedding column not set.');
   119→      return;
   120→    }
   121→    setIsLoading(true);
   122→    try {
   123→      // Explicitly type the response from triggerEmbeddingApi if it's not inferred correctly
   124→      const embeddingResponse: { job_id: string } = await triggerEmbeddingApi(fileId, embeddingSelection.selectedColumn, embeddingSelection.modelName);
   125→      setJobId(embeddingResponse.job_id);
   126→      message.success(`Embedding process started with Job ID: ${embeddingResponse.job_id}`);
   127→      setCurrentStep(currentStep + 1); // Move to DoneStep
   128→    } catch (err: any) {
   129→      console.error('Error triggering embedding:', err);
   130→      message.error(`Embedding trigger failed: ${err.message}`); // err.response?.data?.detail removed
   131→    } finally {
   132→      setIsLoading(false);
   133→    }
   134→  };
   135→
   136→  const back = () => setCurrentStep(currentStep - 1);
   137→
   138→  const steps = [
   139→    {
   140→      title: 'Select Source',
   141→      content: <SelectSourceStep 
   142→                  onNext={handleFileSelectedAndUpload} 
   143→                  setUploadedFile={setUploadedFile} 
   144→                />,
   145→    },
   146→    {
   147→      title: 'Preview and select Fields',
   148→      content: <PreviewFieldsStep 
   149→                  onNext={(data) => handlePreviewAndFieldsNext(data as EmbeddingSelection)} 
   150→                  onBack={back} 
   151→                  fileColumnInfo={fileColumnInfo} 
   152→                  currentSelection={embeddingSelection}
   153→                  setSelection={setEmbeddingSelection} // Allow PreviewStep to directly update for more complex interactions
   154→                />,
   155→    },
   156→    {
   157→      title: 'Review',
   158→      content: <ReviewStep 
   159→                  onNext={handleReviewAndTriggerEmbedding} 
   160→                  onBack={back} 
   161→                  uploadedFile={uploadedFile} 
   162→                  embeddingSelection={embeddingSelection} 
   163→                />,
   164→    },
   165→    {
   166→      title: 'Done',
   167→      content: <DoneStep onBack={back} onFinish={onClose} jobId={jobId}/>,
   168→    },
   169→  ];
   170→
   171→  return (
   172→    <Card className="max-w-4xl mx-auto mt-10">
   173→      <Title level={2} className="text-center mb-6">Add Data</Title>
   174→      <Steps current={currentStep} className="mb-8">
   175→        {steps.map(item => (
   176→          <Step key={item.title} title={item.title} />
   177→        ))}
   178→      </Steps>
   179→      {isLoading ? (
   180→        <div className="p-10 text-center">
   181→          <Spin size="large" tip={loadingMessage} />
   182→        </div>
   183→      ) : (
   184→        <div>{steps[currentStep].content}</div>
   185→      )}
   186→    </Card>
   187→  );
   188→};
   189→
   190→export default AddDataWizard; 

