   246          }
   247        );
   248
   249        if (!response.ok) {
   250          throw new Error(`Failed to fetch visualization: ${response.status} ${response.statusText}`); 
   251        }
   252
   253        const data: VisualizationData[] = await response.json();
   254
   255        if (signal.aborted) return;
   256
   257        if (data.length > 0) {
   258          const vis = data;
   259          setVisualizationData(vis); // Store the raw visualization data
   260          const transformedPoints = transformVisualizationToPoints(vis);
   261          setPoints(transformedPoints);
