   263          setUniqueClusters(Array.from(new Set(transformedPoints.map(p => p.cluster))).sort((a, b) => a - b));
   264        } else {
   265          // No specific visualization found for this method/dimension
   266          setVisualizationData(null);
   267          setPoints([]);
   268          setFilteredPoints([]);
   269          setUniqueClusters([]);
   270          // Optional: Set a specific warning/info message here if needed
   271        }
   272      } catch (err) {
   273        if (!signal.aborted) {
   274          console.error('Error fetching visualization:', err);
   275          setError(err instanceof Error ? err.message : 'An unknown fetch error occurred.');
   276          setVisualizationData(null);
   277          setPoints([]);
   278          setFilteredPoints([]);
   279          setUniqueClusters([]);
   280        }
   281      } finally {
   282        if (!signal.aborted) {
   283          setIsLoadingData(false);
   284        }
   285      }
   286    };
   287
   288    fetchVisualizations();
   289
   290    // Cleanup function
   291    return () => {
   292      abortController.abort();
   293    };
   294  }, [embeddingId, selectedMethod, selectedDimensions, isProcessing]); // Re-fetch if params change or processing finishes
   295
   296  // New tooltip handler function
   297  const handleTooltip = (point: Point): string => {
   298    setHoveredPoint(point);
