   309  // Effect: Initialize and Update Scatterplot
   310  useEffect(() => {
   311    const container = plotContainerRef.current;
   312    if (!container || isLoadingData || filteredPoints.length === 0) {
   313      // If loading, no data, or container not ready, destroy existing plot if any
   314      if (scatterplotInstanceRef.current) {
   315        scatterplotInstanceRef.current.destroy?.();
   316        scatterplotInstanceRef.current = null;
