   419            Step: {progress.current_step.replace(/_/g, ' ')}
   420          </Text>
   421        )}
   422        <Progress
   423          percent={progress?.progress ?? 0}
   424          status={progress?.status === 'failed' ? 'exception' : 'active'}
   425          style={{ marginBottom: '10px' }}
   426        />
   427         {progress?.status === 'failed' && (
   428            <Alert
   429                message="Processing Failed"
