     9const { Title, Text } = Typography;
    10
    11// --- Constants ---
    12const CHART_PARENT_ID = 'deep-scatter-parent-element-id';
    13const POLLING_INTERVAL_MS = 2000;
    14const PLOT_INIT_DELAY_MS = 100; // Delay to potentially allow container dimensions to stabilize
    15
    16const METHODS = [
    17  { label: 'UMAP', value: 'umap' },
    18  { label: 'PCA', value: 'pca' },
    19];
    20const DIMENSIONS = [
    21  { label: '2D', value: 2 },
    22  { label: '3D', value: 3 },
    23];
    24
    25// --- Interfaces ---
    26interface Point {
    27  x: number;
    28  y: number;
    29  z?: number;
    30  cluster: number;
    31}
    32
    33interface VisualizationData {
    34  method: string;
    35  dimensions: number;
    36  visualization_id: number;
    37  embedding_id: number;
    38  file_id: number;
    39  row_id: number;
    40  coordinates: number[];
    41  clusters: number;
    42  created_at: string;
    43}
    44
    45interface ProgressInfo {
    46  status: 'pending' | 'processing' | 'complete' | 'failed';
    47  progress: number;
    48  current_step?: string;
    49  error?: {
    50    error: string;
    51    traceback?: string; // Make traceback optional as it might not always be present
