   357→                                type="primary"
   358→                                icon={<LinkOutlined />}
   359→                                href={`${VISUALIZATION_BASE}/embeddings/${fileId}`}
   360→                                target="_blank"
   361→                              >
   362→                                View Visualization
   363→                              </Button>
   364→                            )}
   365→                          </div>
   366→                          
   367→                          <List
   368→                            dataSource={jobs.slice(0, 3)}
   369→                            renderItem={(job) => (
   370→                              <List.Item>
   371→                                <div className="flex items-center justify-between w-full p-3 bg-gray-50 rounded-lg">
   372→                                  <Space>
   373→                                    {getStatusIcon(job.status)}
   374→                                    <div>
   375→                                      <Space>
   376→                                        <Text strong className="text-sm">
   377→                                          Job: {job.job_id.slice(0, 8)}...
   378→                                        </Text>
   379→                                        <Tag color={getStatusColor(job.status)}>
   380→                                          {job.status}
   381→                                        </Tag>
   382→                                      </Space>
   383→                                      {job.model_name && (
   384→                                        <div>
   385→                                          <Text type="secondary" className="text-xs">
   386→                                            Model: {job.model_name}
   387→                                          </Text>
   388→                                        </div>
   389→                                      )}
   390→                                      {job.error && (
   391→                                        <div>
   392→                                          <Text type="danger" className="text-xs">
   393→                                            Error: {job.error.error}
   394→                                          </Text>
   395→                                        </div>
   396→                                      )}
   397→                                    </div>
   398→                                  </Space>
   399→                                  
   400→                                  {job.progress !== null && (
   401→                                    <Text type="secondary" className="text-sm">
   402→                                      {job.progress}%
   403→                                    </Text>
   404→                                  )}
   405→                                </div>
   406→                              </List.Item>
   407→                            )}
   408→                          />
   409→                          
   410→                          {jobs.length > 3 && (
   411→                            <div className="text-center py-2">
   412→                              <Text type="secondary" className="text-sm">
   413→                                + {jobs.length - 3} more job{jobs.length - 3 !== 1 ? 's' : ''}
   414→                              </Text>
   415→                            </div>
   416→                          )}
   417→                        </Card>
   418→                      );
   419→                    })}
   420→                  </div>
   421→                )}
   422→              </div>
   423→            </div>
   424→          </>
   425→        ) : (
   426→          <div className="flex-1 flex items-center justify-center">
   427→            <Empty
   428→              image={<FolderOutlined style={{ fontSize: 64, color: '#d9d9d9' }} />}
   429→              description={
   430→                <div>
   431→                  <Title level={4} type="secondary">No Project Selected</Title>
   432→                  <Text type="secondary">Select a project from the sidebar to view its details</Text>
   433→                </div>
   434→              }
   435→            />
   436→          </div>
   437→        )}
