   439→
   440→      {/* Create Project Modal */}
   441→      <Modal
   442→        title="Create New Project"
   443→        open={showCreateModal}
   444→        onOk={createProject}
   445→        onCancel={() => {
   446→          setShowCreateModal(false);
   447→          setNewProject({ name: '', description: '' });
   448→        }}
   449→        okText="Create Project"
   450→        cancelText="Cancel"
   451→        okButtonProps={{
   452→          disabled: !newProject.name.trim()
   453→        }}
   454→      >
