     1→import React, { useState, useEffect } from 'react';
     2→import { 
     3→  PlusOutlined, 
     4→  FolderOutlined, 
     5→  FileTextOutlined, 
     6→  LinkOutlined, 
     7→  DeleteOutlined, 
     8→  ReloadOutlined, 
     9→  ExclamationCircleOutlined, 
    10→  CheckCircleOutlined,
    11→  LoadingOutlined
    12→} from '@ant-design/icons';
    13→import { 
    14→  Button, 
    15→  Modal, 
    16→  Input, 
    17→  Card, 
    18→  List, 
    19→  Typography, 
    20→  Space, 
    21→  Tag, 
    22→  Alert, 
    23→  Spin, 
    24→  Empty,
    25→  Tooltip,
    26→  message
    27→} from 'antd';
    28→const { Title, Text, Paragraph } = Typography;
    29→const { TextArea } = Input;
    30→
    31→const API_BASE = 'http://localhost:8000';
    32→const VISUALIZATION_BASE = 'http://localhost:5173';
    33→
    34→const ProjectDashboard = () => {
    35→  const [projects, setProjects] = useState([]);
    36→  const [selectedProject, setSelectedProject] = useState(null);
    37→  const [embeddingJobs, setEmbeddingJobs] = useState([]);
    38→  const [showCreateModal, setShowCreateModal] = useState(false);
    39→  const [loading, setLoading] = useState(false);
    40→  const [jobsLoading, setJobsLoading] = useState(false);
    41→  const [newProject, setNewProject] = useState({ name: '', description: '' });
    42→
    43→  // Default headers for API calls
    44→  const defaultHeaders = {
    45→    'Authorization': 'Bearer asdasd',
    46→    'Accept': 'application/json',
    47→    'Content-Type': 'application/json'
    48→  };
    49→
    50→  // Fetch projects on component mount
    51→  useEffect(() => {
    52→    fetchProjects();
    53→  }, []);
    54→
    55→  // Fetch embedding jobs when a project is selected
    56→  useEffect(() => {
    57→    if (selectedProject) {
    58→      fetchEmbeddingJobs(selectedProject.project_id);
    59→    }
    60→  }, [selectedProject]);
    61→
    62→  const fetchProjects = async () => {
    63→    try {
    64→      setLoading(true);
    65→      const response = await fetch(`/api/projects/`, {
    66→        headers: defaultHeaders
    67→      });
    68→      
    69→      if (!response.ok) {
    70→        throw new Error(`HTTP error! status: ${response.status}`);
    71→      }
    72→      
    73→      const data = await response.json();
    74→      setProjects(data);
    75→      
    76→      // Select first project by default
    77→      if (data.length > 0 && !selectedProject) {
    78→        setSelectedProject(data[0]);
    79→      }
    80→    } catch (err) {
    81→      message.error(`Error fetching projects: ${err.message}`);
    82→    } finally {
    83→      setLoading(false);
    84→    }
    85→  };
    86→
    87→  const fetchEmbeddingJobs = async (projectId) => {
    88→    try {
    89→      setJobsLoading(true);
    90→      const response = await fetch(`/api/embeddings/projects/${projectId}/jobs`, {
    91→        headers: defaultHeaders
    92→      });
    93→      
    94→      if (!response.ok) {
    95→        throw new Error(`HTTP error! status: ${response.status}`);
    96→      }
    97→      
    98→      const data = await response.json();
    99→      setEmbeddingJobs(data);
   100→    } catch (err) {
   101→      message.error(`Error fetching embedding jobs: ${err.message}`);
   102→    } finally {
   103→      setJobsLoading(false);
   104→    }
   105→  };
   106→
   107→  const createProject = async () => {
   108→    if (!newProject.name.trim()) {
   109→      message.warning('Please enter a project name');
   110→      return;
   111→    }
   112→    
   113→    try {
   114→      const response = await fetch(`/api/projects/`, {
   115→        method: 'POST',
   116→        headers: defaultHeaders,
   117→        body: JSON.stringify(newProject)
   118→      });
   119→      
   120→      if (!response.ok) {
   121→        throw new Error(`HTTP error! status: ${response.status}`);
   122→      }
   123→      
   124→      const data = await response.json();
   125→      setProjects(prev => [...prev, data]);
   126→      setSelectedProject(data);
   127→      setShowCreateModal(false);
   128→      setNewProject({ name: '', description: '' });
   129→      message.success('Project created successfully');
   130→    } catch (err) {
   131→      message.error(`Error creating project: ${err.message}`);
   132→    }
   133→  };
   134→
   135→  const deleteProject = async (projectId) => {
   136→    Modal.confirm({
   137→      title: 'Delete Project',
   138→      content: 'Are you sure you want to delete this project?',
   139→      okText: 'Delete',
   140→      okType: 'danger',
   141→      cancelText: 'Cancel',
   142→      onOk: async () => {
   143→        try {
   144→          const response = await fetch(`/api/projects/${projectId}`, {
   145→            method: 'DELETE',
   146→            headers: defaultHeaders
   147→          });
   148→          
   149→          if (!response.ok) {
   150→            throw new Error(`HTTP error! status: ${response.status}`);
   151→          }
   152→          
   153→          setProjects(prev => prev.filter(p => p.project_id !== projectId));
   154→          if (selectedProject?.project_id === projectId) {
   155→            setSelectedProject(projects.length > 1 ? projects[0] : null);
   156→          }
   157→          message.success('Project deleted successfully');
   158→        } catch (err) {
   159→          message.error(`Error deleting project: ${err.message}`);
   160→        }
   161→      }
   162→    });
   163→  };
   164→
   165→  const getStatusIcon = (status) => {
   166→    switch (status) {
   167→      case 'complete':
   168→        return <CheckCircleOutlined style={{ color: '#52c41a' }} />;
   169→      case 'progress_unavailable (unknown)':
   170→        return <ExclamationCircleOutlined style={{ color: '#faad14' }} />;
   171→      default:
   172→        return <LoadingOutlined style={{ color: '#1890ff' }} />;
   173→    }
   174→  };
   175→
   176→  const getStatusColor = (status) => {
   177→    switch (status) {
   178→      case 'complete':
   179→        return 'success';
   180→      case 'progress_unavailable (unknown)':
   181→        return 'warning';
   182→      default:
   183→        return 'processing';
   184→    }
   185→  };
   186→
   187→  // Group embedding jobs by file_id
   188→  const groupedJobs = embeddingJobs.reduce((acc, job) => {
   189→    if (!acc[job.file_id]) {
   190→      acc[job.file_id] = [];
   191→    }
   192→    acc[job.file_id].push(job);
   193→    return acc;
   194→  }, {});
   195→
   196→  return (
   197→    <div className="flex h-screen bg-gray-50">
   198→      {/* Sidebar */}
   199→      <div className="w-80 bg-white border-r border-gray-200 flex flex-col">
   200→        <div className="p-6 border-b border-gray-200">
   201→          <div className="flex items-center justify-between mb-4">
   202→            <Title level={3} className="!mb-0">Projects</Title>
   203→            <Button
   204→              type="primary"
   205→              icon={<PlusOutlined />}
   206→              onClick={() => setShowCreateModal(true)}
   207→              title="Create New Project"
   208→            />
   209→          </div>
   210→        </div>
   211→
   212→        <div className="flex-1 overflow-y-auto p-4">
   213→          {loading && projects.length === 0 ? (
   214→            <div className="text-center py-8">
   215→              <Spin size="large" />
   216→              <div className="mt-4">
   217→                <Text type="secondary">Loading projects...</Text>
   218→              </div>
   219→            </div>
   220→          ) : (
   221→            <>
   222→              {projects.length === 0 ? (
   223→                <Empty
   224→                  image={<FolderOutlined style={{ fontSize: 48, color: '#d9d9d9' }} />}
   225→                  description={
   226→                    <div>
   227→                      <Text type="secondary">No projects yet</Text>
   228→                      <br />
   229→                      <Text type="secondary" className="text-sm">
   230→                        Create your first project to get started
   231→                      </Text>
   232→                    </div>
   233→                  }
   234→                />
   235→              ) : (
   236→                <List
   237→                  dataSource={projects}
   238→                  renderItem={(project) => (
   239→                    <List.Item className="!px-0">
   240→                      <Card
   241→                        hoverable
   242→                        className={`w-full cursor-pointer transition-all ${
   243→                          selectedProject?.project_id === project.project_id
   244→                            ? 'border-blue-500 shadow-md'
   245→                            : ''
   246→                        }`}
   247→                        onClick={() => setSelectedProject(project)}
   248→                        actions={[
   249→                          <Tooltip title="Delete Project">
   250→                            <Button
   251→                              type="text"
   252→                              icon={<DeleteOutlined />}
   253→                              danger
