    27→    }
    28→    // It's important to return a Promise.reject to pass the error to the calling code
    29→    return Promise.reject(error);
    30→  }
    31→);
    32→
    33→// Add a request interceptor to dynamically add auth tokens
    34→apiClient.interceptors.request.use((config: InternalAxiosRequestConfig) => {
    35→  const token = localStorage.getItem('authToken') || 'asdasd'; // Fallback to hardcoded token for now
    36→  if (token && config.headers) {
    37→    config.headers.Authorization = `Bearer ${token}`;
