     1→import React, { useState, useEffect } from 'react';
     2→import { useNavigate } from 'react-router-dom';
     3→import { 
     4→  PlusOutlined, 
     5→  FolderOutlined, 
     6→  FileTextOutlined, 
     7→  EyeOutlined,
     8→  PlayCircleOutlined,
     9→  ReloadOutlined,
    10→  SettingOutlined,
    11→  SearchOutlined,
    12→  CalendarOutlined,
    13→  DatabaseOutlined
    14→} from '@ant-design/icons';
    15→import { 
    16→  Button, 
    17→  Card, 
    18→  List, 
    19→  Typography, 
    20→  Space, 
    21→  Tag, 
    22→  Spin, 
    23→  Empty,
    24→  message,
    25→  Layout,
    26→  Descriptions,
    27→  Input,
    28→  Row,
    29→  Col,
    30→  Statistic,
    31→  Badge,
    32→  Tooltip,
    33→  Avatar,
    34→  Divider
    35→} from 'antd';
    36→
    37→const { Title, Text, Paragraph } = Typography;
    38→const { Header, Sider, Content } = Layout;
    39→const { Search } = Input;
    40→
    41→interface Project {
    42→  project_id: number;
    43→  name: string;
    44→  description: string;
    45→  user_id: number;
    46→  created_at: string;
    47→}
    48→
    49→interface ProjectFile {
    50→  file_id: number;
    51→  filename: string;
    52→  original_filename: string;
    53→  file_type: string;
    54→  project_id: number;
    55→  row_count: number;
    56→  created_at: string;
    57→  columns: {
    58→    names: string[];
    59→    types: Record<string, string>;
    60→    sample_size: number;
    61→    numeric_columns: string[];
    62→    text_columns: string[];
    63→  };
    64→}
    65→
    66→const API_BASE = 'http://localhost:8000/api';
    67→const AUTH_TOKEN = 'asdasd';
    68→
    69→const Main: React.FC = () => {
    70→  const navigate = useNavigate();
    71→  const [projects, setProjects] = useState<Project[]>([]);
    72→  const [selectedProject, setSelectedProject] = useState<Project | null>(null);
    73→  const [projectFiles, setProjectFiles] = useState<ProjectFile[]>([]);
    74→  const [loading, setLoading] = useState(false);
    75→  const [filesLoading, setFilesLoading] = useState(false);
    76→  const [searchQuery, setSearchQuery] = useState('');
    77→
    78→  // Fetch projects on component mount
    79→  useEffect(() => {
    80→    fetchProjects();
    81→  }, []);
    82→
    83→  // Fetch files when project is selected  
    84→  useEffect(() => {
    85→    if (selectedProject) {
    86→      fetchProjectFiles(selectedProject.project_id);
    87→    }
    88→  }, [selectedProject]);
    89→
    90→  const fetchProjects = async () => {
    91→    try {
    92→      setLoading(true);
    93→      const response = await fetch(`/api/projects/`, {
    94→        headers: {
    95→          'Authorization': `Bearer ${AUTH_TOKEN}`,
    96→          'accept': 'application/json'
    97→        }
    98→      });
    99→      
   100→      if (!response.ok) {
   101→        throw new Error(`HTTP error! status: ${response.status}`);
   102→      }
   103→      
   104→      const data = await response.json();
   105→      setProjects(data);
   106→      
   107→      // Select first project by default
   108→      if (data.length > 0 && !selectedProject) {
   109→        setSelectedProject(data[0]);
   110→      }
   111→    } catch (err) {
   112→      message.error(`Error fetching projects: ${err.message}`);
   113→    } finally {
   114→      setLoading(false);
   115→    }
   116→  };
   117→
   118→  const fetchProjectFiles = async (projectId: number) => {
   119→    try {
   120→      setFilesLoading(true);
   121→      const response = await fetch(`/api/files/project/${projectId}`, {
   122→        headers: {
   123→          'Authorization': `Bearer ${AUTH_TOKEN}`,
   124→          'accept': 'application/json'
   125→        }
   126→      });
   127→      
   128→      if (!response.ok) {
   129→        throw new Error(`HTTP error! status: ${response.status}`);
   130→      }
   131→      
   132→      const data = await response.json();
   133→      setProjectFiles(Array.isArray(data) ? data : [data]);
   134→    } catch (err) {
   135→      message.error(`Error fetching project files: ${err.message}`);
   136→      setProjectFiles([]);
   137→    } finally {
   138→      setFilesLoading(false);
   139→    }
   140→  };
   141→
   142→  const checkVisualizationExists = async (fileId: number): Promise<boolean> => {
   143→    try {
   144→      const response = await fetch(`/api/visualizations/file/${fileId}?method=umap&dimensions=2`, {
   145→        headers: {
   146→          'Authorization': `Bearer ${AUTH_TOKEN}`,
   147→          'accept': 'application/json'
   148→        }
   149→      });
   150→      return response.ok;
   151→    } catch (err) {
   152→      return false;
   153→    }
   154→  };
   155→
   156→  const handleFileClick = async (file: ProjectFile) => {
   157→    try {
   158→      const hasVisualization = await checkVisualizationExists(file.file_id);
   159→      
   160→      if (hasVisualization) {
   161→        // Navigate to visualization
   162→        navigate(`/visualizations/file/${file.file_id}?method=umap&dimensions=2`);
   163→      } else {
   164→        // Route to add-data for processing
   165→        navigate('/add-data');
   166→      }
   167→    } catch (err) {
   168→      message.error('Error checking visualization status');
   169→      navigate('/add-data');
   170→    }
   171→  };
   172→
   173→  const generateEmbedding = async (fileId: number, column: string) => {
   174→    try {
   175→      const response = await fetch(`/api/embeddings/${fileId}/generate?column=${column}&model_name=openai-text-embedding-ada-002`, {
   176→        method: 'POST',
   177→        headers: {
   178→          'Authorization': `Bearer ${AUTH_TOKEN}`,
   179→          'accept': 'application/json'
   180→        },
   181→        body: ''
   182→      });
   183→      
   184→      if (!response.ok) {
   185→        throw new Error(`HTTP error! status: ${response.status}`);
   186→      }
   187→      
   188→      const data = await response.json();
   189→      message.success(`Embedding generation started. Job ID: ${data.job_id}`);
   190→    } catch (err) {
   191→      message.error(`Error generating embedding: ${err.message}`);
   192→    }
   193→  };
   194→
   195→  // Filter files based on search query
   196→  const filteredFiles = projectFiles.filter(file =>
   197→    file.original_filename.toLowerCase().includes(searchQuery.toLowerCase()) ||
   198→    file.file_type.toLowerCase().includes(searchQuery.toLowerCase())
   199→  );
   200→
   201→  // Calculate statistics
   202→  const totalFiles = projectFiles.length;
   203→  const totalRows = projectFiles.reduce((sum, file) => sum + file.row_count, 0);
   204→  const fileTypes = [...new Set(projectFiles.map(file => file.file_type))];
   205→  const hasTextColumns = projectFiles.some(file => file.columns.text_columns.length > 0);
   206→
   207→  return (
   208→    <Layout className="min-h-screen">
   209→      {/* Top Bar */}
   210→      <Header className="bg-white border-b border-gray-200 px-6 flex items-center justify-between">
   211→        <div className="flex items-center">
   212→          <Title level={3} className="!mb-0 text-blue-600">EmbedLens</Title>
   213→        </div>
   214→        <Button 
   215→          type="primary" 
   216→          icon={<PlusOutlined />}
   217→          onClick={() => {
   218→            const projectId = selectedProject?.project_id || 1;
   219→            navigate(`/add-data?project=${projectId}`);
   220→          }}
   221→        >
   222→          Add Data
   223→        </Button>
   224→      </Header>
   225→
   226→      <Layout>
   227→        {/* Left Sidebar - Projects */}
   228→        <Sider width={320} className="bg-white border-r border-gray-200">
   229→          <div className="p-4 border-b border-gray-200">
   230→            <Title level={4} className="!mb-0">Projects</Title>
   231→          </div>
   232→          
   233→          <div className="p-4">
   234→            {loading ? (
   235→              <div className="text-center py-8">
   236→                <Spin size="large" />
   237→                <div className="mt-4">
   238→                  <Text type="secondary">Loading projects...</Text>
   239→                </div>
   240→              </div>
   241→            ) : projects.length === 0 ? (
   242→              <Empty
   243→                image={<FolderOutlined style={{ fontSize: 48, color: '#d9d9d9' }} />}
   244→                description="No projects found"
   245→              />
   246→            ) : (
   247→              <List
   248→                dataSource={projects}
   249→                renderItem={(project) => (
   250→                  <List.Item className="!px-0 !py-2">
   251→                    <Card
   252→                      hoverable
   253→                      size="small"
   254→                      className={`w-full cursor-pointer transition-all ${
   255→                        selectedProject?.project_id === project.project_id
   256→                          ? 'border-blue-500 bg-blue-50'
   257→                          : ''
   258→                      }`}
   259→                      onClick={() => setSelectedProject(project)}
   260→                    >
   261→                      <div className="flex items-start">
   262→                        <FolderOutlined className="text-blue-500 mr-3 mt-1" />
   263→                        <div className="flex-1 min-w-0">
   264→                          <Text strong className="block truncate">{project.name}</Text>
   265→                          <Text type="secondary" className="text-xs block truncate">
   266→                            ID: {project.project_id}
   267→                          </Text>
   268→                          {project.description && (
   269→                            <Text type="secondary" className="text-xs block truncate">
   270→                              {project.description}
   271→                            </Text>
   272→                          )}
   273→                        </div>
   274→                      </div>
   275→                    </Card>
   276→                  </List.Item>
   277→                )}
   278→              />
   279→            )}
   280→          </div>
   281→        </Sider>
   282→
   283→        {/* Main Content - Files */}
   284→        <Content className="bg-gray-50">
   285→          {selectedProject ? (
   286→            <div className="p-6">
   287→              <div className="flex items-center justify-between mb-6">
   288→                <div>
   289→                  <Title level={2} className="!mb-1">{selectedProject.name}</Title>
   290→                  <Text type="secondary">Project ID: {selectedProject.project_id}</Text>
   291→                  {selectedProject.description && (
   292→                    <>
   293→                      <br />
   294→                      <Text type="secondary">{selectedProject.description}</Text>
   295→                    </>
   296→                  )}
   297→                </div>
   298→                <Button
   299→                  icon={<ReloadOutlined />}
   300→                  onClick={() => fetchProjectFiles(selectedProject.project_id)}
   301→                  title="Refresh files"
   302→                />
   303→              </div>
   304→
   305→              <div className="bg-white rounded-lg shadow-sm">
   306→                <div className="p-6 border-b border-gray-200">
   307→                  <Title level={3} className="!mb-0">Files</Title>
   308→                </div>
   309→                
   310→                <div className="p-6">
   311→                  {filesLoading ? (
   312→                    <div className="text-center py-12">
   313→                      <Spin size="large" />
   314→                      <div className="mt-4">
   315→                        <Text type="secondary">Loading files...</Text>
   316→                      </div>
   317→                    </div>
   318→                  ) : projectFiles.length === 0 ? (
   319→                    <Empty
   320→                      image={<FileTextOutlined style={{ fontSize: 48, color: '#d9d9d9' }} />}
   321→                      description={
   322→                        <div>
   323→                          <Text type="secondary">No files in this project</Text>
   324→                          <br />
   325→                          <Button 
   326→                            type="primary" 
   327→                            icon={<PlusOutlined />}
   328→                            onClick={() => navigate(`/add-data?project=${selectedProject.project_id}`)}
   329→                            className="mt-4"
   330→                          >
   331→                            Add Files
   332→                          </Button>
   333→                        </div>
   334→                      }
   335→                    />
   336→                  ) : (
   337→                    <div className="space-y-4">
   338→                      {projectFiles.map((file) => (
   339→                        <Card 
   340→                          key={file.file_id}
   341→                          hoverable
   342→                          className="cursor-pointer"
   343→                          onClick={() => handleFileClick(file)}
   344→                        >
   345→                          <div className="flex items-start justify-between">
   346→                            <Space align="start">
   347→                              <FileTextOutlined className="text-blue-500 text-xl" />
   348→                              <div>
   349→                                <Text strong className="text-lg">{file.original_filename}</Text>
   350→                                <br />
   351→                                <Text type="secondary" className="text-sm">
   352→                                  File ID: {file.file_id} • {file.row_count.toLocaleString()} rows • {file.file_type.toUpperCase()}
   353→                                </Text>
   354→                                <br />
   355→                                <Text type="secondary" className="text-xs">
   356→                                  Created: {new Date(file.created_at).toLocaleDateString()}
   357→                                </Text>
   358→                              </div>
   359→                            </Space>
   360→                            
   361→                            <Space direction="vertical" align="end">
   362→                              <Button
   363→                                type="primary"
   364→                                icon={<EyeOutlined />}
   365→                                onClick={(e) => {
   366→                                  e.stopPropagation();
   367→                                  handleFileClick(file);
   368→                                }}
   369→                              >
   370→                                View
   371→                              </Button>
   372→                              {file.columns.text_columns.length > 0 && (
   373→                                <Button
   374→                                  icon={<PlayCircleOutlined />}
   375→                                  onClick={(e) => {
   376→                                    e.stopPropagation();
   377→                                    // Use first text column as default
   378→                                    generateEmbedding(file.file_id, file.columns.text_columns[0]);
   379→                                  }}
   380→                                >
   381→                                  Generate Embeddings
   382→                                </Button>
   383→                              )}
   384→                            </Space>
   385→                          </div>
   386→                          
   387→                          {/* Column Information */}
   388→                          <div className="mt-4 pt-4 border-t border-gray-100">
   389→                            <Descriptions size="small" column={1}>
   390→                              {file.columns.text_columns.length > 0 && (
   391→                                <Descriptions.Item label="Text Columns">
   392→                                  <div className="flex flex-wrap gap-1">
   393→                                    {file.columns.text_columns.map(col => (
   394→                                      <Tag key={col} color="blue" className="text-xs">{col}</Tag>
   395→                                    ))}
   396→                                  </div>
   397→                                </Descriptions.Item>
   398→                              )}
   399→                              {file.columns.numeric_columns.length > 0 && (
   400→                                <Descriptions.Item label="Numeric Columns">
   401→                                  <div className="flex flex-wrap gap-1">
   402→                                    {file.columns.numeric_columns.map(col => (
   403→                                      <Tag key={col} color="green" className="text-xs">{col}</Tag>
   404→                                    ))}
   405→                                  </div>
   406→                                </Descriptions.Item>
   407→                              )}
   408→                              <Descriptions.Item label="Sample Size">
   409→                                <Text className="text-xs">{file.columns.sample_size.toLocaleString()} rows</Text>
   410→                              </Descriptions.Item>
   411→                            </Descriptions>
   412→                          </div>
   413→                        </Card>
   414→                      ))}
   415→                    </div>
   416→                  )}
   417→                </div>
   418→              </div>
   419→            </div>
   420→          ) : (
   421→            <div className="flex items-center justify-center h-full">
   422→              <Empty
   423→                image={<FolderOutlined style={{ fontSize: 64, color: '#d9d9d9' }} />}
   424→                description={
   425→                  <div>
   426→                    <Title level={4} type="secondary">Select a Project</Title>
   427→                    <Text type="secondary">Choose a project from the sidebar to view its files</Text>
   428→                  </div>
   429→                }
   430→              />
   431→            </div>
   432→          )}
   433→        </Content>
   434→      </Layout>
   435→    </Layout>
   436→  );
   437→};
   438→
   439→export default Main;

