     1→import axios, { AxiosResponse, AxiosError, InternalAxiosRequestConfig } from 'axios';
     2→
     3→// Determine the base URL for the API. 
     4→// This might come from an environment variable or be hardcoded if it's stable.
     5→const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000/api/v1';
     6→
     7→export const apiClient = axios.create({
     8→  baseURL: API_BASE_URL,
     9→  headers: {
    10→    'Content-Type': 'application/json',
    11→    // You can add other default headers here, like Authorization tokens if needed
    12→    // For example, if you have a token in localStorage:
    13→    // 'Authorization': localStorage.getItem('token') ? `Bearer ${localStorage.getItem('token')}` : undefined,
    14→  },
    15→});
    16→
    17→// Optional: Add a response interceptor for global error handling or token refresh logic
    18→apiClient.interceptors.response.use(
    19→  (response: AxiosResponse) => response, // Simply return the response if it's successful
    20→  (error: AxiosError) => {
    21→    // Handle errors globally here
    22→    // For example, redirect to login on 401 Unauthorized
    23→    if (error.response && error.response.status === 401) {
    24→      // Handle unauthorized access, e.g., redirect to login page
    25→      // window.location.href = '/login';
    26→      console.error('Unauthorized access (401). Consider redirecting to login or token refresh.');
    27→    }
    28→    // It's important to return a Promise.reject to pass the error to the calling code
    29→    return Promise.reject(error);
    30→  }
    31→);
    32→
    33→// Add a request interceptor to dynamically add auth tokens
    34→apiClient.interceptors.request.use((config: InternalAxiosRequestConfig) => {
    35→  const token = localStorage.getItem('authToken') || 'asdasd'; // Fallback to hardcoded token for now
    36→  if (token && config.headers) {
    37→    config.headers.Authorization = `Bearer ${token}`;
    38→  }
    39→  return config;
    40→});
    41→
    42→export default apiClient; 

