  PlayCircleOutlined,
  ReloadOutlined,
  SettingOutlined,
  SearchOutlined,
  CalendarOutlined,
  DatabaseOutlined
} from '@ant-design/icons';
import { 
  Button, 
  Card, 
  List, 
  Typography, 
  Space, 
  Tag, 
  Spin, 
  Empty,
  message,
  Layout,
  Descriptions,
  Input,
  Row,
  Col,
  Statistic,
  Badge,
  Tooltip,
  Avatar,
  Divider
} from 'antd';

const { Title, Text, Paragraph } = Typography;
const { Header, Sider, Content } = Layout;
const { Search } = Input;

interface Project {
  project_id: number;
  name: string;
  description: string;
  user_id: number;
  created_at: string;
}

interface ProjectFile {
  file_id: number;
  filename: string;
  original_filename: string;
  file_type: string;
  project_id: number;
  row_count: number;
