const fetchProjects = useCallback(async () => {
  try {
    setLoading(true);
    setError(null);
    const response = await apiClient.get('/projects/');
    const data = response.data;

    setProjects(data);

    if (data.length > 0 && !selectedProject) {
      setSelectedProject(data[0]);
    }
  } catch (err: any) {
    const errorMessage = err.response?.data?.detail || err.message || 'Failed to fetch projects';
    setError(errorMessage);
    message.error(`Error fetching projects: ${errorMessage}`);
  } finally {
    setLoading(false);
  }
}, [selectedProject]);

const fetchProjectFiles = useCallback(async (projectId: number) => {
  try {
    setFilesLoading(true);
    setError(null);
    const response = await apiClient.get(`/files/project/${projectId}`);
    const data = response.data;

    setProjectFiles(Array.isArray(data) ? data : [data]);
  } catch (err: any) {
    const errorMessage = err.response?.data?.detail || err.message || 'Failed to fetch project files';
    setError(errorMessage);
    message.error(`Error fetching project files: ${errorMessage}`);
    setProjectFiles([]);
  } finally {
    setFilesLoading(false);
  }
}, []);

const checkVisualizationExists = useCallback(async (fileId: number): Promise<boolean> => {
  try {
    const response = await apiClient.get(`/visualizations/file/${fileId}?method=umap&dimensions=2`);
    return response.status === 200 && response.data.length > 0;
  } catch (err: any) {
    return false;
  }
}, []);

const handleFileClick = useCallback(async (file: ProjectFile) => {
  try {
    const hasVisualization = await checkVisualizationExists(file.file_id);

    if (hasVisualization) {
      navigate(`/embeddings/${file.file_id}`);
    } else {
      navigate(`/add-data?project=${file.project_id}&file=${file.file_id}`);
    }
  } catch (err: any) {
    message.error('Error checking visualization status');
    navigate('/add-data');
  }
}, [navigate, checkVisualizationExists]);

const generateEmbedding = useCallback(async (request: EmbeddingGenerationRequest) => {
  try {
    const response = await apiClient.post(
      `/embeddings/${request.fileId}/generate?column=${request.column}&model_name=${request.modelName}`,
      {}
    );

    const data = response.data;
    message.success(`Embedding generation started. Job ID: ${data.job_id}`);

    navigate(`/job/${data.job_id}/progress`);
  } catch (err: any) {
    const errorMessage = err.response?.data?.detail || err.message || 'Failed to generate embedding';
    message.error(`Error generating embedding: ${errorMessage}`);
  }
}, [navigate]);

const handleRefreshProjects = useCallback(() => {
  fetchProjects();
}, [fetchProjects]);

const handleRefreshFiles = useCallback(() => {
  if (selectedProject) {
    fetchProjectFiles(selectedProject.project_id);
  }
}, [selectedProject, fetchProjectFiles]);

