const filteredFiles = useMemo(() => {
  if (!searchQuery.trim()) return projectFiles;

  const query = searchQuery.toLowerCase();
  return projectFiles.filter(file =>
    file.original_filename.toLowerCase().includes(query) ||
    file.file_type.toLowerCase().includes(query) ||
    file.columns.text_columns.some(col => col.toLowerCase().includes(query)) ||
    file.columns.numeric_columns.some(col => col.toLowerCase().includes(query))
  );
}, [projectFiles, searchQuery]);

const projectStats = useMemo((): ProjectStats => {
  const now = new Date();
  const oneWeekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

  const recentFiles = projectFiles.filter(file => 
    new Date(file.created_at) > oneWeekAgo
  ).length;

  return {
    totalFiles: projectFiles.length,
    totalRows: projectFiles.reduce((sum, file) => sum + file.row_count, 0),
    fileTypes: [...new Set(projectFiles.map(file => file.file_type))],
    hasTextColumns: projectFiles.some(file => file.columns.text_columns.length > 0),
    avgRowsPerFile: projectFiles.length > 0 ? 
      Math.round(projectFiles.reduce((sum, file) => sum + file.row_count, 0) / projectFiles.length) : 0,
    recentFiles
  };
}, [projectFiles]);

const getFileIcon = useCallback((fileType: string) => {
  switch (fileType.toLowerCase()) {
    case 'csv':
      return <FileExcelOutlined style={{ color: token.colorSuccess }} />;
    case 'json':
      return <DatabaseOutlined style={{ color: token.colorInfo }} />;
    default:
      return <FileTextOutlined style={{ color: token.colorPrimary }} />;
  }
}, [token]);

const getFileTypeColor = useCallback((fileType: string) => {
  switch (fileType.toLowerCase()) {
    case 'csv':
      return 'green';
    case 'json':
      return 'blue';
    case 'xlsx':
      return 'orange';
    default:
      return 'default';
  }
}, []);

const handleEmbeddingGeneration = useCallback((file: ProjectFile) => {
  setEmbeddingModal({ visible: true, file });
}, []);

const handleEmbeddingModalOk = useCallback((column: string, modelName: string) => {
  if (embeddingModal.file) {
    generateEmbedding({
      fileId: embeddingModal.file.file_id,
      column,
      modelName
    });
    setEmbeddingModal({ visible: false, file: null });
  }
}, [embeddingModal.file, generateEmbedding]);

const handleEmbeddingModalCancel = useCallback(() => {
  setEmbeddingModal({ visible: false, file: null });
}, []);

